import { createSlice } from "@reduxjs/toolkit";

export const CntDocSlice = createSlice({
    name: "CntDocSlice",
    initialState: {
        value: 1,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = CntDocSlice.actions;

export const selectConCnt = (state) => state.cntDoc.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default CntDocSlice.reducer;
