import { createSlice } from "@reduxjs/toolkit";

export const CntCivilSlice = createSlice({
    name: "CntCivilSlice",
    initialState: {
        value: 4,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = CntCivilSlice.actions;

export const selectConCnt = (state) => state.cntCivil.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default CntCivilSlice.reducer;
