import React, { useState, useEffect, useRef } from "react";
import RoomItem from "./RoomItem";
import {
  Container,
  Box,
  Paper,
  Grid,
  Button,
  Stack,
  ClickAwayListener,
  Tooltip,
  TextField,
} from "@mui/material";
import { useSelector } from "react-redux";

import SearchIcon from "@mui/icons-material/Search";
import ClearIcon from "@mui/icons-material/Clear";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";

import axios from "axios";
const dummy = [
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
  {
    roomSeq: 100,
    hostUser: 2,
    title: "",
    capacity: 1,
    participants: 6,
    locked: false,
  },
];
function RoomList() {
  const me = useSelector((state) => state.user);
  // 1. 방 목록 객체 배열
  const roomLists = useSelector((state) => state.roomList);
  console.log("roomLists: ", roomLists);
  const roomList = useRef();

  useEffect(() => {
    let cnt = Object.keys(roomLists).length;
    for (let i = 1; i <= dummy.length; i++) {
      roomLists[cnt + i] = dummy[i - 1];
    }
    console.log("after work:", roomLists);
    roomList.current = roomLists;
    console.log("roomList.current: ", roomList.current);
  }, []);

  // 2. 방 페이징
  const [page, setPage] = React.useState(1);
  const beforePage = () => {
    setPage(page - 1);
  };
  const afterPage = () => {
    setPage(page + 1);
  };
  // 3. 방검색
  const [searchResult, setSearchResult] = useState([]);
  const [searchYes, setSearchYes] = useState(false);
  // 방 검색 실패 tooltip
  const [doNot, setDoNot] = useState(false);
  const DoNotOpen = () => setDoNot(true);
  const DoNotClose = () => setDoNot(false);
  const [searchTitle, setSearchTitle] = useState("");
  const searchRoom = () => {
    if (searchTitle) {
      axios
        .post(
          "/api/room/list",
          {
            capacity: 6,
            mafiaNum: 1,
            title: searchTitle,
          },
          {
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${me.accessToken}`,
            },
          }
        )
        .then((res) => {
          console.log("방 검색 axios", res.data);
          setSearchTitle("");
          if (res.data.length) {
            setSearchYes(true);
            setSearchResult(res.data);
          } else {
            DoNotOpen();
          }
        })
        .catch((err) => {
          console.log("실패", err);
        });
    }
  };

  return (
    <Paper sx={{ width: "100%", height: "457px", backgroundColor: "rgba(30,30,30,0.9)" }}></Paper>
  );
}

export default RoomList;

