import React, { useState } from "react";
import {
  Paper,
  Stack,
  Button,
  Container,
  TextField,
  Modal,
  Box,
  Typography,
  InputLabel,
  MenuItem,
  FormControl,
  Select,
} from "@mui/material";

// 아이콘
import AddIcon from "@mui/icons-material/Add";
import ShuffleIcon from "@mui/icons-material/Shuffle";
import SearchIcon from "@mui/icons-material/Search";
import RefreshIcon from "@mui/icons-material/Refresh";
import LibraryBooksIcon from "@mui/icons-material/LibraryBooks";
import SettingsIcon from "@mui/icons-material/Settings";

import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { roomNum } from "../../redux/slice/RoomNumSlice";
import { roomList } from "../../redux/slice/RoomListSlice";

import { Helper } from "../RoomNavCom/Helper";

// 모달 창 style
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 500,
  bgcolor: "background.paper",
  border: "1px solid #000",
  boxShadow: 24,
};
const Logo = styled.a`
  display: flex;
  flex-direction: row;
  cursor: pointer;
  img {
    display: block;
    width: 30px;
  }
`;

// 방만들기(모달), 랜덤매칭, 방검색(검색창 옆으로), 새로고침, 환경설정(모달), 도움말(모달)
function Nav() {
  const dispatch = useDispatch();
  const [SwitchModalValue, setSwitchModalValue] = useState(0);
  const [open, setOpen] = useState(false);
  const handleOpen = value => {
    setSwitchModalValue(value);
    setOpen(true);
  };
  const handleClose = () => setOpen(false);
  const SwitchModals = () => {
    if (SwitchModalValue === 0) {
      return <Helper />;
    }
  };
  const me = useSelector(state => state.user);
  // 방 검색
  const [search, setSearch] = useState(false);

  // 방 생성 모달
  const [openRoom, setOpenRoom] = useState(false);
  const HandleOpenRoom = () => {
    setOpenRoom(true);
  };
  const HandleCloseRoom = () => {
    setOpenRoom(false);
  };
  const navigate = useNavigate();
  // 방 제목
  const [roomTitle, setRoomTitle] = useState("");
  // 방 비밀번호 유무
  const [ox, setOx] = useState("false");
  // 방 비밀번호
  const [passWord, setPassWord] = useState("");
  // 방 최대인원수
  const [num, setNum] = useState(0);
  const handleChange = event => {
    setNum(event.target.value);
  };
  const GoRoom = () => {
    console.log(roomTitle);
    const data = {
      roomInfo: {
        roomSeq: 0,
        capacity: num, // 방 최대 인원
        hostUser: me.userSeq,
        isLocked: ox, // 비밀번호 유무
        password: passWord, // 암호
        title: roomTitle,
      },
      gameInfo: {
        // 게임 설정이 담겨있는 DTO
        gameInfoSeq: 0, // 아무 값이나 입력해주세요, DB Primary Key 및 게임 정보 식별자로 방 생성시 자동할당 됩니다.
        day: 0, // 낮 시간
        night: 0, // 밤시간
        doctorNum: 0, // 의사 수
        mafiaNum: 0, // 마피아 수
        policeNum: 0, // 경찰 수
        talkTimeoutSec: 0, // 낮 토론 시간 (단위 sec)
        voteTimeoutSec: 0, // 밤 토론 시간 (단위 sec)
      },
    };
    console.log(data);
    // 방 제목, 인원수 설정을 해야 방 생성 가능
    if (roomTitle && num) {
      if (passWord) {
        setOx(true);
      }
      axios
        .post("/api/room", data, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${me.accessToken}`,
          },
        })
        .then(response => {
          // 방번호 리덕스 저장
          console.log(response.data);
          alert(`${response.data.roomInfo.roomSeq}방 입장`)
          dispatch(roomNum(response.data.roomInfo.roomSeq));
          HandleCloseRoom();
          setNum(0);
          setRoomTitle("");
        });
    }
  };

  const searchClick = () => {
    setSearch(!search);
  };

  const [searchTitle, setSearchTitle] = useState("");
  // 방 검색
  const searchRoom = () => {
    console.log(searchTitle)
    axios
      .post(
        "/api/room/list",
        {
          capacity: 6,
          mafiaNum: 1,
          title: searchTitle,
        },
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${me.accessToken}`,
          },
        }
      )
      .then(res => {
        setSearchTitle('')
        if (res.data.length){
        console.log("검색성공", res.data)}
        else {
          console.log('검색된 방이 없음')
        }
      })
      .catch(err => {
        console.log("검색실패", err);
      });
  };

  // 룸리스트 새로고침
  const getNewList = () => {
    // 방 목록
    axios
      .get("/api/room/list", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${me.accessToken}`,
        },
      })
      .then(response => {
        dispatch(roomList(response.data));
      });
  };
  return (
    <Paper sx={{ mt: 2, width: "1500px" }}>
      <Container sx={{ p: 1, mx: 0 }}>
        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing={2}>
          <Stack direction='row' justifyContent='center' alignItems='center' spacing={2}>
            <Button variant='outlined' onClick={HandleOpenRoom}>
              <AddIcon />
            </Button>
            <Button variant='outlined'>
              <ShuffleIcon />
            </Button>
            {search ? (
              <Stack direction='row'>
                <TextField
                  fullWidth
                  id='outlined-textarea'
                  placeholder='Search'
                  size='small'
                  value = {searchTitle}
                  onChange={e => {
                    setSearchTitle(e.target.value);
                  }}
                />
                <Button variant='outlined' onClick={searchRoom}>
                  <SearchIcon />
                </Button>
              </Stack>
            ) : (
              <Button variant='outlined' onClick={searchClick}>
                <SearchIcon />
              </Button>
            )}
          </Stack>
          <Stack direction='row' justifyContent='center' alignItems='center' spacing={2}>
            <Button variant='outlined' onClick={getNewList}>
              <RefreshIcon />
            </Button>
            <Button
              size='small'
              onClick={() => {
                handleOpen(0);
              }}
              variant='outlined'
            >
              <LibraryBooksIcon />
            </Button>
            <Button variant='outlined'>
              <SettingsIcon />
            </Button>
          </Stack>
        </Stack>
      </Container>

      {/* 방 생성 모달 */}
      <Modal
        open={openRoom}
        onClose={HandleCloseRoom}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={style}>
          <Box
            sx={{
              width: 502,
              height: 31,
              backgroundColor: "var(--color-5);",
            }}
          >
            <Logo>
              <img src='logo.svg' alt='logo' />
            </Logo>
          </Box>
          <Container>
            <Typography
              id='modal-modal-title'
              variant='h6'
              component='h2'
              sx={{
                my: 2,
                display: "flex",
                justifyContent: "center",
              }}
            >
              방 만들기
            </Typography>
            <span>방제목</span>
            <input
              type='text'
              placeholder='방 제목을 입력하세요'
              onChange={e => {
                setRoomTitle(e.target.value);
              }}
            />
            <span>비밀번호</span>
            <input
              type='password'
              id='password'
              placeholder='비밀번호를 입력하세요'
              onChange={e => {
                setPassWord(e.target.value);
              }}
            />
            <Box sx={{ minWidth: 120 }}>
              <FormControl fullWidth>
                <InputLabel id='demo-simple-select-label'>게임 인원 수</InputLabel>
                <Select
                  labelId='demo-simple-select-label'
                  id='demo-simple-select'
                  value={num}
                  label='게임 인원 수'
                  onChange={handleChange}
                >
                  <MenuItem value={6}>6</MenuItem>
                  <MenuItem value={7}>7</MenuItem>
                  <MenuItem value={8}>8</MenuItem>
                </Select>
              </FormControl>
            </Box>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-around",
                p: 1,
                m: 1,
                bgcolor: "background.paper",
                borderRadius: 1,
              }}
            >
              <Button variant='outlined' onClick={GoRoom}>
                방 생성
              </Button>
              <Button
                variant='outlined'
                onClick={HandleCloseRoom}
                size='small'
                sx={{ width: "25%" }}
              >
                취소
              </Button>
            </Box>
          </Container>
        </Box>
      </Modal>
      <Modal open={open} onClose={handleClose}>
        <Box sx={style}>
          <SwitchModals />
        </Box>
      </Modal>
    </Paper>
  );
}
export default Nav;
