import React, { useEffect, useState } from "react";
import { Container, styleButton, styleModal, styleTextField } from '../style.js';
import { Modal, Box, Button, TextField } from '@mui/material';
import { useNavigate } from  "react-router-dom";
import styled from 'styled-components';
import axios from 'axios';


const FindPasswordPage = (props) => {
  const navigate = useNavigate();

  const [emailAuthentication, setEmailAuthentication] = useState(false);
  const [resendEmail, setResendEmailOpen] = useState(false);
  const [emailCode, setEmailCode] = useState('');
  const [modalOpen, setModalOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const [userSeq, setUserSeq] = useState(null);
  const [changePassword, setChangePassword] = useState(false);
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [isDone, setIsDone] = useState(false);

  useEffect(() => {
    if (isDone && !modalOpen) {
      navigate('/signin');
    }
  });

  const handleResendEmailOpen = () => {
    setResendEmailOpen(true);
  };
  const handleResendEmailClose = () => {
    setResendEmailOpen(false);
  };

  const goEmailAuthentication = () => {
    //   handleModalOpen('이메일을 확인해주세요.');  // 유효성 검사
    sendEmail();
    setEmailAuthentication(true);
  };

  const sendEmail = () => {
    axios({
      method: 'post',
      url: '/api/sendEmail',
      headers: {
        'Content-Type': 'text/plain',
      },
      data: email,
    })
    .then((response) => {
      // console.log(response.data);
      setUserSeq(response.data)  // 유저 시퀀스 받아오기
      // console.log('이메일 보냄');
    })
    .catch(error => {
      if (error.response.status === 401) {
        // setWrongInputData(true);
      }
    })
  }

  const checkEmailCode = () => {
    
    axios({
      method: 'put',
      url: `/api/emailValidationUser/${userSeq}`,
      headers: {
        'Content-Type': 'application/json',
      },
      data: emailCode,
    })
    .then((response) => {
      // console.log(response.data);
      if (response.data) {
        // setModalOpen(false);
        // navigate('/ChangePasswordPage');
        setChangePassword(true);
      } else {
        handleModalOpen('인증 코드가 일치하지 않습니다.');
      }
    })
    .catch(error => {
      if (error.response.status === 401) {
        // setWrongInputData(true);
      }
    })
  }
  
  const updateUserPassword = () => {
    if (isSamePassword()) {
      axios({
        method: 'post',
        url: '/api/changePw',
        headers: {
          'Content-Type': 'application/json',
        },
        data: {
          email,
          password
        },
      })
      .then((response) => {
        handleModalOpen('비밀번호가 변경되었습니다.');
        setIsDone(true);
        // setChangePassword(true);
      })
      .catch(error => {
        if (error.response.status === 401) {
          // setWrongInputData(true);
        }
      })
    } else {
      handleModalOpen('비밀번호가 다릅니다.');
    }
  }

  const isSamePassword = () => {
    return password === passwordCheck;
  };

  const handleModalOpen = (message) => {
    setModalMessage(message);
    setModalOpen(true);
  };
  const handleModalClose = () => {
    setModalOpen(false);
  };

  return (
    <>
      <Modal
        open={modalOpen}
        onClose={handleModalClose}
        aria-labelledby="modal-title"
      >
        <Box sx={{ ...styleModal, width: 400 }}>
          <h2 id="modal-title">{modalMessage}</h2>
          <Button style={styleButton} onClick={handleModalClose}>확인</Button>
        </Box>
      </Modal>

      { changePassword ?
        <Container>
          <h2>비밀번호 변경</h2>
          <Content>
            <label htmlFor="password">비밀번호</label>
            <TextField style={styleTextField} type="password" id="password" name="password" onChange={(e) => {setPassword(e.target.value)}}></TextField>
          </Content>
          <Content>
            <label htmlFor="password-check">비밀번호 확인</label>
            <TextField style={styleTextField} type="password" id="password-check" name="password-check" onChange={(e) => {setPasswordCheck(e.target.value)}}></TextField>
          </Content>

          {!isSamePassword() ? <span>비밀번호가 다릅니다.</span> : null}

          <Button style={styleButton} onClick={updateUserPassword}>비밀번호 변경</Button>

        </Container>
      :
        ( emailAuthentication ?
          <Container>
            <Content>
              <div>
                <h4>인증 메일이 발송되었습니다.</h4>
                <span>
                <p>메일함에서 인증 메일을 확인하시기 바랍니다.</p>
                {/* <p>이메일의 인증 버튼을 누르면 비밀번호 변경이 가능합니다.</p> */}
                <p>이메일의 코드를 입력하면 비밀번호 변경이 가능합니다.</p>
                </span>
              </div>
              <TextField style={styleTextField} id="emailCode" name="emailCode" placeholder="이메일 코드" onChange={(e) => {setEmailCode(e.target.value);}}></TextField>
              <Button style={styleButton} onClick={() => {checkEmailCode()}}>확인</Button>
            </Content>
            
            <Button style={styleButton} onClick={() => {sendEmail(); handleResendEmailOpen();}}>이메일 재전송</Button>
            {/* <Link to="/changepassword">메일 인증 시</Link>     비밀번호 변경 페이지 확인용(백 연결 시 지우기) */}
            
            <Modal
              open={resendEmail}
              onClose={handleResendEmailClose}
              aria-labelledby="modal-title"
            >
              <Box sx={{ ...styleModal, width: 400 }}>
                <h2 id="modal-title">인증 메일을 재전송했습니다.</h2>
                <Button style={styleButton} onClick={handleResendEmailClose}>확인</Button>
              </Box>
            </Modal>
          </Container>
        :
          <Container>
            <h2>비밀번호 찾기</h2>
            <Content>
              <label htmlFor="email">이메일</label>
              <TextField style={styleTextField} id="email" name="email" placeholder="이메일" onChange={(e) => {setEmail(e.target.value)}}></TextField>
              {/* <Button style={styleButton} onClick={() => {if (email.length) goEmailAuthentication(); else handleModalOpen('이메일을 입력해주세요.');}}>중복검사</Button> */}
            </Content>
          
            <Button style={styleButton} onClick={() => {if (email.length) goEmailAuthentication(); else handleModalOpen('이메일을 입력해주세요.');}}>이메일 전송</Button>
          </Container>
        )
      }
    </>
  );
};

const Content = styled.div`
  overflow: hidden;
  display: block;
  text-align: center;
`

export default FindPasswordPage;
