import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import {
    Table,
    TableContainer,
    TableBody,
    Paper,
    TableRow,
    TableCell,
    Button,
    Container,
} from "@mui/material";

function Chat({ Chats, Send }) {
    const username = useSelector((state) => state.user.nickname);
    // 보내는 채팅
    const [userChat, setUserChat] = useState({
        header: {
            type: "chat",
        },
        data: {
            nickname: username,
            message: "",
        },
    });
    // 채팅 적기
    const handleMessage = (event) => {
        const { value } = event.target;
        setUserChat({
            ...userChat,
            data: {
                nickname: username,
                message: value,
            },
        });
    };

    // 채팅 전송
    const sendValue = () => {
        if (userChat.data.message){
            Send(userChat);
        }

        setUserChat({
            ...userChat,
            data: {
                nickname: username,
                message: "",
            },
        });
    };
    // 엔터 키 입력
    const handleOnKeyPress = (e) => {
        if (e.key === "Enter" && userChat.data.message) {
            sendValue(userChat); // Enter 입력이 되면 클릭 이벤트 실행
            setUserChat({
                ...userChat,
                data: {
                    nickname: username,
                    message: "",
                },
            });
        }
    };

    return (
        <Container>
            <TableContainer
                style={{ padding: "20px", height: "100px" }}
                component={Paper}
            >
                <Table size="medium">
                    <TableBody>
                        {Chats &&
                            Chats.map((chat, index) => (
                                <TableRow key={index}>
                                    <TableCell>
                                        {chat.data.nickname} :{" "}
                                    </TableCell>
                                    <TableCell>{chat.data.message}</TableCell>
                                </TableRow>
                            ))}
                    </TableBody>
                </Table>
            </TableContainer>
            <input
                type="text"
                style={{ height: 20, width: "100%" }}
                value={userChat.data.message}
                onChange={handleMessage}
                onKeyPress={handleOnKeyPress}
            />
            <Button
                variant="outlined"
                sx={{ ml: 1, height: 25 }}
                onClick={sendValue}
            >
                send
            </Button>
        </Container>
    );
}
const UL = styled.section`
    list-style: none;
    display: flex;
    flex-direction: column;
`;
const Border = styled.section`
    border: 1px solid rgba(0, 0, 0, 0.2);
    border-radius: 5px;
    margin: 0.5em;
`;

export default Chat;
