import React, { useState } from "react";
import styled from "styled-components";
import {
  Container,
  Button,
  Modal,
  Box,
  Typography,
  TextField,
  Stack,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../CSS/PulseButton.css";

import { useSelector, useDispatch } from "react-redux";
import { enrollNickname } from "../redux/slice/UserSlice";
import { roomList } from "../redux/slice/RoomListSlice";
import { friendList } from "../redux/slice/FriendListSlice";


// 모달 창 style
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "gray",
  color:"white",
  border: "1px solid #000",
  boxShadow: 24,
  // position: 'absolute',
  // top: '50%',
  // left: '50%',
  // transform: 'translate(-50%, -50%)',
  // width: 400,
  // color:'white',
  // bgcolor: 'gray',  
  // border: '2px solid #000',
  // boxShadow: 24,
  // p: 4,
  // borderRadius: '20px'
};

const Logo = styled.a`
  display: flex;
  flex-direction: row;
  cursor: pointer;
  img {
    display: block;
    width: 30px;
  }
`;

const MainPage = () => {
  // redux에 내 정보 가져오기
  const me = useSelector(state => state.user);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  // 닉네임 중복
  const [dup, setDup] = useState(false);

  // 로그인은 되었지만 닉네임이 없는 경우
  const [openTwo, setOpenTwo] = useState(false);
  // 닉네임
  const [nickname, setNewNickname] = useState("");

  const HandleOpenTwo = () => {
    setOpenTwo(true);
  };
  const HandleCloseTwo = () => {
    setOpenTwo(false);
  };
  // // 게임 시작 시 데이터 redux 올리기
  const Data = () => {
    // 방 목록
    axios
      .get("/api/room/list", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${me.accessToken}`,
        },
      })
      .then(response => {
        console.log("방 목록 : ", response.data);
        dispatch(roomList(response.data));
      });
    // 친구 목록
    axios
      .get("/api/user/friend/friend-list", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${me.accessToken}`,
        },
      })
      .then(response => {
        dispatch(friendList(response.data));
      });

    navigate("/roomList");
  };

  // 상태 구분
  const HandleOpen = () => {
    // 로그인 안 된 상태
    if ((me.accessToken === undefined) | (me.accessToken === null)) {
      return navigate("/signin");
    } // 로그인 되었지만 닉네임이 없어
    else {
      if (me.nickname === null) {
        return HandleOpenTwo();
      } // 로그인 되고 닉네임 있음
      else {
        return navigate("/roomList"), Data();
      }
    }
  };

  // 닉네임 입력
  const InputNickname = event => {
    setNewNickname(event.target.value);
  };

  // 닉네임 중복 검사
  const CheckNickname = () => {
    axios({
      method: "post",
      url: "/api/user/checkNickname",
      headers: {
        "Content-Type": "text/plain",
        Authorization: `Bearer ${me.accessToken}`,
      },
      data: nickname,
    })
      .then(response => {
        // 사용 가능
        if (!response.data) {
          setDup(true);
          alert("사용 가능한 닉네임입니다!");
        } else {
          setDup(false);
          alert("이미 존재하는 닉네임입니다!");
        }
      })
      .catch(err => console.log("실패퍂패", err));
  };

  // 게임 시작 버튼
  const CheckGameGo = () => {
    // 중복 검사 통과
    if (dup) {
      // 닉네임 등록
      axios({
        method: "put",
        url: "/api/user/enrollNickname",
        headers: {
          "Content-Type": "text/plain",
          Authorization: `Bearer ` + me.accessToken,
        },
        data: nickname,
      }).then(() => {
        dispatch(enrollNickname(nickname));
        // 정보 받아오기
        Data();
        navigate("/roomList");
      });
    } else {
      alert("닉네임 등록이 필요합니다.");
    }
  };
  return (
    <Container maxWidth='lg'>
      <Stack>
        <Box>
          <button className='pulse-button' variant='outlined' onClick={HandleOpen}>
            Game Start
          </button>
        </Box>
      </Stack>

      <Modal
        open={openTwo}
        onClose={HandleCloseTwo}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={{...style, borderRadius: '20px'}}>
          <Box
            sx={{
              width: 402,
              height: 31,
              backgroundColor: "var(--color-5);",
            }}
          >
            <Logo>
              <img src='logo.svg' alt='logo' />
            </Logo>
          </Box>
          <Container>
            <Typography
              id='modal-modal-title'
              variant='h6'
              component='h2'
              sx={{
                my: 2,
                display: "flex",
                justifyContent: "center",
              }}
            >
              닉네임 등록이 필요합니다.
            </Typography>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-between",
                m: 1,
                my: 3,
                bgcolor: "gray",
                borderRadius: 1,
              }}
            >
              <TextField
                fullWidth
                id='nickname'
                placeholder="닉네임"
                size='small'
                onChange={InputNickname}
                sx={{ width: "100%", mr: 1}}
              />
              <Button
                variant='outlined'
                onClick={CheckNickname}
                style={{color:"white",border: 'solid 2px var(--color-2)',
                        backgroundColor: 'var(--color-5)',
                        padding: '2px 10px',
                        borderRadius: '6px',
                        fontSize: '16px',
                        marginLeft: '10px',
                        cursor: 'pointer',
                        width:"40%",
                        textDecoration: 'none',}}
              >
                중복검사
              </Button>
            </Box>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-around",
                p: 1,
                m: 1,
                bgcolor: "gray",
                borderRadius: 1,
              }}
            >
              <Button variant='outlined' onClick={CheckGameGo}
              style={{color:"white",border: 'solid 2px var(--color-2)',
              backgroundColor: 'var(--color-5)',
              padding: '2px 10px',
              borderRadius: '6px',
              fontSize: '16px',
              marginLeft: '10px',
              cursor: 'pointer',
              width:"40%",
              textDecoration: 'none',}}
              >
                게임 시작
              </Button>
              <Button
                variant='outlined'
                onClick={HandleCloseTwo}
                size='small'
                sx={{ width: "25%" }}
                style={{color:"white",border: 'solid 2px var(--color-2)',
                        backgroundColor: 'var(--color-5)',
                        padding: '2px 10px',
                        borderRadius: '6px',
                        fontSize: '16px',
                        marginLeft: '10px',
                        cursor: 'pointer',
                        width:"40%",
                        textDecoration: 'none',}}
              >
                취소
              </Button>
            </Box>
          </Container>
        </Box>
      </Modal>
    </Container>
  );
};

export default MainPage;
