import React,{useEffect,useState} from "react";
import { Container, styleButton } from '../style.js';
import { useParams } from  "react-router-dom";
import { Button } from '@mui/material';
import { useNavigate } from  "react-router-dom";
import {useSelector} from "react-redux"
import axios from 'axios';
import { Box, Stack } from "@mui/system";
import Loading from "../components/Loading.js";



const NoticeDetailPage = () => {

  const [noticeData, setNoticeData] = useState(null);
  const token = useSelector(state=>state.user.accessToken)
  const isAdmin = useSelector((state) => state.user.authority) === "ROLE_ADMIN" ? true : false;

  const { noticeId } = useParams();
  const navigate = useNavigate();

  const goNoticeListPage = () => {
    navigate("/board");
  };

  const deleteNotice = () => {
    axios.delete(`/api/admin/board/${noticeId}`,{
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    }).then(goNoticeListPage)
  } 

  const goUpdateNoticePage = () => {
    navigate(`/board/${noticeId}/update`, {state : noticeData});
  };
  useEffect(() => {
    axios.get(`/api/board/${noticeId}`,{})
    .then(response => {
      setNoticeData(response.data);
    });
  }, []);




  return (
    <Container style={{fontSize:"24px"}}>
      {noticeData === null ? <Loading /> :<>
        <div style={{
          textAlign:"left",
          overflow:"auto",
          backgroundColor: "rgba(220,220,220,0.3)",
          width:"70%", height:"80%"}}>
          <h1 style={{color:"white"}} >제목:{noticeData.title}</h1>
          <hr />
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <span style={{color:"white"}} >작성자:{noticeData.writer}</span>
            <span style={{color:"white"}}>{(noticeData.createAt+"").substring(0,10)}</span>
          </Box>
          <hr />
          <br />
          <p
          style={{ color:"white", width:"100%", height:"60%"}}
          >{noticeData.content}</p>
          
        </div>
        <span>
          {isAdmin ? 
          <>
            <Button style={styleButton} onClick={goUpdateNoticePage}>수정하기</Button>
            <Button style={styleButton} onClick={deleteNotice}>삭제하기</Button>
          </> : null} 
          <Button style={styleButton} onClick={goNoticeListPage}>목록으로</Button>
        </span>
      </>}
    </Container>
  );
};

export default NoticeDetailPage;
