import { createSlice } from "@reduxjs/toolkit";

export const GameTimeSlice = createSlice({
  name: "GameTime",
  initialState: {
    talkTime: 60,
    voteTime: 60,
    nightTime: 30,
  },
  reducers: {
    setTalkTime: (state, action) => {
      state.talkTime = action.payload;
    },
    setVoteTime: (state, action) => {
      state.voteTime = action.payload;
    },
    setNightTime: (state, action) => {
      state.nightTime = action.payload;
    },
    selectTalkTime: state => state.gameTime.talkTime,
    selectVoteTime: state => state.gameTime.voteTime,
    selectNightTime: state => state.gameTime.nightTime,
  },
});

export const {
  setTalkTime,
  setVoteTime,
  setNightTime,
  selectTalkTime,
  selectVoteTime,
  selectNightTime,
} = GameTimeSlice.actions;

export default GameTimeSlice.reducer;
