import React, { useState, useEffect } from "react";
import Nav from "../components/RoomListPageCom/Nav";
import SideBar from "../components/RoomListPageCom/SideBar";
import RoomList from "../components/RoomListPageCom/RoomList";
import MyProfile from "../components/RoomListPageCom/MyProfile";
import Chat from "../components/RoomListPageCom/Chat";
import { Container, Grid, Box } from "@mui/material";
import styled from "styled-components";

import SockJS from "sockjs-client";
import { over } from "stompjs";

import { useSelector } from "react-redux";

var stompClient = null;
function RoomListPage() {
  const me = useSelector((state) => state.user);
  let headers = {
    token: `${me.accessToken}`,
  };
  // 채팅 관련 데이터
  const [Chats, setChats] = useState([]); // 채팅 목록
  // 로그인 유저 목록
  const [loginUser, setLoginUser] = useState([]);

  // roomListPage 들어오면 연결
  useEffect(() => {
    connect();
    return () => {
      stompClient.disconnect();
    };
  }, []);
  const connect = () => {
    let Sock = new SockJS("https://i7d106.p.ssafy.io:8080/ws");
    stompClient = over(Sock);
    stompClient.debug = null;
    stompClient.connect({}, onConnected, onError);
  };
  // 구독 연결
  const onConnected = () => {
    stompClient.subscribe("/sub/lobby", Received);
    const data = {
      header: {
        type: "join",
      },
      data: {
        token: me.accessToken,
        nickname: me.nickname,
      },
    };
    stompClient.send("/pub/lobby", headers, JSON.stringify(data));
  };

  const onError = (err) => {
    console.log(err);
  };
  // 서버로부터 받기
  const Received = (payload) => {
    var payloadData = JSON.parse(payload.body); // JSON 문자열을 JavaScript 객체로 변환
    if (payloadData.header.type === "chat") {
      Chats.push(payloadData);
      setChats([...Chats]);
    } else if (payloadData.header.type === "join") {
      setLoginUser(payloadData.data.users);
    }
  };

  // 소켓 전송 채팅
  const Send = (data) => {
    if (stompClient.ws.readyState === 1) {
      console.log("보낸다");
      return stompClient.send("/pub/lobby", headers, JSON.stringify(data));
    }
  };
  // 소켓 전송 친구관련
  const SendF = (f, type) => {
    if (stompClient.ws.readyState === 1) {
      var sendData = {
        header: {
          type,
        },
        data: {
          friendSeq: null,
          from: me.nickname,
          to: f.nickname,
        },
      };
      return stompClient.send(`/pub/friend/${f.nickname}`, headers, JSON.stringify(sendData));
    }
  };

  return (
    <Container sx={{ height: "100vh", display: "flex", alignItems: "center", minWidth: "1200px" }}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <Nav />
        </Grid>
        <Grid item xs={4}>
          <SideBar loginUser={loginUser} SendF={SendF} />
        </Grid>
        <Grid item xs={8}>
          <RoomList />
        </Grid>
        <Grid item xs={4}>
          <MyProfile />
        </Grid>
        <Grid item xs={8}>
          <Chat Chats={Chats} Send={Send} />
        </Grid>
      </Grid>
    </Container>
  );
}

export default RoomListPage;
