//react
import React, { useEffect, useState } from "react";

//redux
import { useSelector } from "react-redux";
import { setClient, selectWs } from "../../redux/slice/WsSlice";
import { setNightTime, setTalkTime, setVoteTime } from "../../redux/slice/GameTimeSlice";

//mui library
import Grid from "@mui/material/Grid";
import { useDispatch } from "react-redux";
import { selectCntCivil, setCivil } from "../../redux/slice/CntCivilSlice";
import { selectCntCop, setCop } from "../../redux/slice/CntCopSlice";
import { selectCntDoc, setDoc } from "../../redux/slice/CntDocSlice";
import { selectCntLimit, setLimit } from "../../redux/slice/CntLimitSlice";
import { selectCntMaf, setMaf } from "../../redux/slice/CntMafSlice";
import Stack from "@mui/material/Stack";

// import {
//     SetRoomTitle,
//     selectRoomTitle,
// } from "../../redux/slice/RoomTitleSlice";
//의사, 경찰, 마피아수 변경 기능

export const GameSetting = (props) => {
  const dispatch = useDispatch();
  const roomNum = useSelector((state) => state.roomNum);
  const token = useSelector((state) => state.user.accessToken);
  const client = useSelector(selectWs);
  const [ArrLimit] = useState([5, 6, 7, 8]);
  const [StateLimit, setStateLimit] = useState(5);
  const [StateDoc, setStateDoc] = useState(0);
  let actLimit = [0, 1, 2, 3, 4, 5, 6, 7];
  const [StateCop, setStateCop] = useState(0);
  const [StateMaf, setStateMaf] = useState(0);
  const [stateTalkTime, setStateTalkTime] = useState(0);
  const [stateNightTime, setStateNightTime] = useState(0);
  const [statevoteTime, setStateVoteTime] = useState(0);
  const UpdateGameSetting = () => {
    const pubAddr = `/pub/room/${roomNum}`;

    console.log("client: ", client);
    if (StateDoc + StateCop + StateMaf > StateLimit) {
      console.log("역할 초과 합: ", StateDoc + StateCop + StateMaf);
      document.getElementById("checkValue").innerText = "배정한 역할이 너무 많습니다.";
      return;
    } else if (StateMaf === 0) {
      document.getElementById("checkValue").innerText = "마피아는 최소 1명이여야 합니다.";
      return;
    }
    console.log("StateLimit: ", StateLimit);
    dispatch(setLimit(StateLimit));
    dispatch(setDoc(StateDoc));
    dispatch(setCop(StateCop));
    dispatch(setMaf(StateMaf));
    dispatch(setCivil(StateLimit - StateDoc - StateCop - StateMaf));
    dispatch(setStateVoteTime(statevoteTime));
    dispatch(setStateNightTime(stateNightTime));
    dispatch(setStateTalkTime(stateTalkTime));
    console.log("before client pub");
    client.publish({
      destination: pubAddr,
      headers: { token: token, "content-type": "application/json" },
      body: JSON.stringify({
        header: { type: "setting" },
        data: {
          title: null,
          gameSeq: roomNum, // 게임 번호 (방 번호랑 다름)
          capacity: StateLimit, // 방 수용 인원
          mafia: StateMaf, // 마피아 수
          doctor: StateDoc, // 의사 수
          police: StateCop, // 경찰 수
          talkTime: stateTalkTime, // 회의 시간 (초)
          voteTime: statevoteTime, // 투표 시간 (초)
          nightTime: stateNightTime, // 밤 시간 (초)
        },
      }),
      skipContentLengthHeader: true,
    });
    props.setOpen(false);
  };
  return (
    <React.Fragment>
      <Grid
        container
        spacing={2}
        sx={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: 400,
          color: "white",
          bgcolor: "rgba(220,220,220,0.3)",
          border: "2px solid #000",
          boxShadow: 24,
          p: 4,
          borderRadius: "20px",
          height: "50%",
        }}
      >
        <Stack direction='row' spacing={2}>
          {/* 인원 수 클릭 시 해당  */}

          <label>인원 수 : </label>
          <select style={{ marginTop: "20px", height: "30px" }}>
            {ArrLimit.map((data, idx) => {
              return <option key={idx}>{data}</option>;
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>의사 수 : </label>
          <select style={{ marginTop: "20px", height: "30px" }}>
            {actLimit.map((data, idx) => {
              return <option key={idx}>{data}</option>;
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>경찰 수 : </label>
          <select style={{ marginTop: "20px", height: "30px" }}>
            {actLimit.map((data, idx) => {
              return <option key={idx}>{data}</option>;
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>마피아 수 : </label>
          <select
            onChanged={(e) => {
              setStateMaf(e.target.value);
            }}
            style={{ marginTop: "20px", height: "30px" }}
          >
            {actLimit.map((data, idx) => {
              return idx === 1 ? (
                <option selected key={idx}>
                  {data}
                </option>
              ) : (
                <option key={idx}>{data}</option>
              );
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>낮 시간 : </label>
          <input id='talkTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>투표 시간 : </label>
          <input id='voteTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>밤 시간 : </label>
          <input id='nightTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <button onClick={() => UpdateGameSetting()}>확인</button>
        <div id='checkValue' style={{ color: "red" }}></div>
      </Grid>
    </React.Fragment>
  );
};
