import React, { useEffect, useRef, useState } from "react";
import Grid from "@mui/material/Grid";
import { useSelector, useDispatch } from "react-redux";
import { setCivil, selectCntCivil } from "../../redux/slice/CntCivilSlice";
import { setCon, selectCntCon } from "../../redux/slice/CntConSlice";
import { setCop, selectCntCop } from "../../redux/slice/CntCopSlice";
import { setDoc, selectCntDoc } from "../../redux/slice/CntDocSlice";
import { setLimit, selectCntLimit } from "../../redux/slice/CntLimitlice";
import { setMaf, selectCntMaf } from "../../redux/slice/CntMafSlice";
import {
    SetRoomTitle,
    selectRoomTitle,
} from "../../redux/slice/RoomTitleSlice";
//의사, 경찰, 마피아수 변경 기능

export const GameSetting = () => {
    const dispatch = useDispatch();

    const [ArrLimit, setArrLimit] = useState([5, 6, 7, 8]);
    const [Limit, setLimit] = useState(0);
    const [Doc, setDoc] = useState(0);
    let actLimit = [0, 1, 2, 3, 4, 5, 6, 7];
    const B = useRef();
    const [Cop, setCop] = useState(0);
    const [Maf, setMaf] = useState(0);
    const [ArrMaf, setArrMaf] = useState([]);

    const tmp = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        // inputFocus.current.focus();
    }, []);

    const SelectBox = (props) => {
        console.log("props.options : ", props.options);
        console.log("props.setNum: ", props.setNum);
        console.log("setLimit: ", setLimit);
        console.log("props.switch: ", props.switch);

        return (
            <select
                onChange={(e) => {
                    props.setNum(e.target.value);
                    let opts = [];
                    for (let i = 0; i <= e.target.value - 1; i++) {
                        opts.push(i);
                    }
                }}
            >
                {props.options.map((option) => (
                    <option key={option} value={option}>
                        {option}
                    </option>
                ))}
            </select>
        );
    };
    const UpdateGameSetting = () => {
        console.log(Doc + COp);
        if (Number(Doc) + Number(Cop) + Number(Maf) > Number(Limit)) {
            document
                .getElementById("checkValue")
                .innerText("수를 다시 확인해주세요.");
            return;
        }
        // dispatch();
    };
    return (
        <React.Fragment>
            <Grid container spacing={2}>
                <Grid item ws={6}>
                    <Grid>
                        {/* 인원 수 클릭 시 해당  */}
                        <label>인원 수 : </label>
                        <select
                            onChange={(e) => {
                                console.log(e.target.value);
                                setLimit(e.target.value);
                            }}
                        >
                            {ArrLimit.map((data, idx) => {
                                return <option key={idx}>{data}</option>;
                            })}
                        </select>
                    </Grid>
                    <Grid>
                        <label>의사 수 : </label>
                        <select
                            onChange={(e) => {
                                setDoc(e.target.value);
                            }}
                        >
                            {actLimit.map((data, idx) => {
                                return <option key={idx}>{data}</option>;
                            })}
                        </select>
                    </Grid>
                    <Grid>
                        <label>경찰 수 : </label>
                        <select
                            onChange={(e) => {
                                setCop(e.target.value);
                            }}
                        >
                            {actLimit.map((data, idx) => {
                                return <option key={idx}>{data}</option>;
                            })}
                        </select>
                    </Grid>
                    <Grid>
                        <label>마피아 수 : </label>
                        <select
                            onChange={(e) => {
                                setMaf(e.target.value);
                            }}
                        >
                            {actLimit.map((data, idx) => {
                                return <option key={idx}>{data}</option>;
                            })}
                        </select>
                    </Grid>
                </Grid>
                <Grid item ws={6}></Grid>
            </Grid>
            <button onClick={UpdateGameSetting}>확인</button>
            <button>취소</button>
            <div id="checkValue" style={{ color: "red" }}></div>
        </React.Fragment>
    );
};
