import React, { useState, useEffect, useRef, useMemo } from "react";

import { useSelector, useDispatch } from "react-redux";

//자체 컴포넌트
import MainBar from "../components/GatherRoomPageCom/MainBar";
import PlayMap from "../components/GatherRoomPageCom/PlayMap";
import SideBar from "../components/GatherRoomPageCom/SideBar";
import AllCam from "../components/GameRoomPageCom/AllCam";
import NaviBar2 from "../components/GatherRoomPageCom/NaviBar2";
import { selectUser } from "../redux/slice/UserSlice";
import { setClient } from "../redux/slice/WsSlice";

//mui 컴포넌트
import Grid from "@mui/material/Grid";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";

//socktjs
import SockJS from "sockjs-client";
import { setInterval } from "stompjs";

var StompJs = require("@stomp/stompjs");

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};
function useInterval(callback, delay) {
  const savedCallback = useRef();

  // Remember the latest callback.
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  // Set up the interval.
  useEffect(() => {
    function tick() {
      savedCallback.current();
    }
    if (delay !== null) {
      let id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
}

const GatherRoom = () => {
  console.log("GatherRoom 리랜더링 확인");
  const debugTime = 6;
  const dispatch = useDispatch();
  const childComponentRef = useRef();
  const sideComponentRef = useRef();
  const token = useSelector((state) => state.user.accessToken);
  const myNickName = useSelector(selectUser).nickname;
  const roomNum = useSelector((state) => state.roomNum);
  const subAddr = `/sub/room/${roomNum}`;
  const pubAddr = `/pub/room/${roomNum}`;
  const pubGameAddr = `${pubAddr}/game`;
  const [currentGameState, setCurrentGameState] = useState("게임 준비 중...");
  const [IsGameStart, setIsGameStart] = useState(false);
  const [myRole, setMyRole] = useState("");
  const [stateTimer, setStateTimer] = useState(0);
  const timer = useRef();
  const [roleList, setRoleList] = useState([]);
  const client = useMemo(
    () =>
      new StompJs.Client({
        //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
        // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
        connectHeaders: {},
        debug: function (str) {
          console.log(str);
        },
        reconnectDelay: 5000,
        heartbeatIncoming: 4000,
        heartbeatOutgoing: 4000,
      }),
    []
  );
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  client.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };
  // const client = new StompJs.Client({
  //   //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
  //   // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
  //   connectHeaders: {},
  //   debug: function (str) {
  //     console.log(str);
  //   },
  //   reconnectDelay: 5000,
  //   heartbeatIncoming: 4000,
  //   heartbeatOutgoing: 4000,
  // });

  const onMessageReceived = (payload) => {
    let parsedData = JSON.parse(payload.body);
    console.log("game Process data: ");
    console.log(parsedData);
    //게임 시작 신호 수신
    if (parsedData.type === "session-created") {
      setCurrentGameState("세션 생성 완료");
      //게더맵 => 캠 화면 변경
      setIsGameStart(true);
    }
    //게임 시작 신호 수신
    else if (parsedData.type === "game-start") {
      setCurrentGameState("게임 시작");
      //main bar에 낮 시작 문구 작성
    }
    //본인 역할 확인
    else if (parsedData.type === "role") {
      console.log("본인 역할 확인");
      console.log(parsedData);
      // setStateTimer(5);
      setStateTimer(debugTime);
      setMyRole(parsedData.data.role);

      //5초 뒤 역할 확인 소켓 전홍
      setTimeout(() => {
        // clearInterval(intTimer1);
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "role",
            data: {
              role: myRole,
            },
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);
      setCurrentGameState("자신의 역할 확인 중");
    }
    //낮 시간 시작
    else if (parsedData.type === "talk-start") {
      setCurrentGameState("낮이 시작되었습니다.");
      // setStateTimer(parsedData.data.time);
      setStateTimer(debugTime);

      setTimeout(() => {
        // clearInterval(intTimer);
        //낮 시간 종료 신호 전송
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "talk-end",
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);
    }
    //
    else if (parsedData.type === "vote-ready") {
      //  let debugTime = parsedData.data.time;
      // setStateTimer(parsedData.data.time);
      setStateTimer(debugTime);
      setCurrentGameState("처형할 인물에 투표하십시오.");

      //투표 종료 신호 전송
      setTimeout(() => {
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "vote-result",
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);
    }
    //투표 종료, 투표 결과 공개
    else if (parsedData.type === "vote-result") {
      setStateTimer(debugTime);
      setCurrentGameState("투표가 종료되었습니다.");

      console.log("vote-result: ");
      //아무도 투표하지 않았다면
      if (parsedData.data === undefined) {
        console.log("아무도 투표하지 않았습니다.");
      } else {
        console.log("죽은 사람 목록: " + parsedData.data.list);
        console.log("죽은 사람: " + parsedData.data.dead);
        console.log("죽은 사람 직업: " + parsedData.data.role);
      }

      setTimeout(() => {
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "vote-check",
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);
    }
    //밤 시작
    else if (parsedData.type === "night") {
      setCurrentGameState("밤이 시작되었습니다.");
      // setStateTimer(parsedData.data.time);
      setStateTimer(debugTime);
      setTimeout(() => {
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "night-result",
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);
      console.log("night: ", parsedData.data);
    }
    //밤 투표 결과 데이터
    else if (parsedData.type === "night-result") {
      setStateTimer(debugTime);
      console.log("밤 투표 결과 데이터: ");
      // log
      // if(parsedData.data.dead === ){
      //   console.log();
      // }

      setTimeout(() => {
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "night-check",
          }),
          skipContentLengthHeader: true,
        });
        client.publish({
          destination: pubGameAddr,
          headers: { token: token, "content-type": "application/json" },
          body: JSON.stringify({
            type: "night-act",
            data: {
              nickname: "닉네임",
              role: "자신의 역할",
              target: "목표 닉네임",
            },
          }),
          skipContentLengthHeader: true,
        });
      }, debugTime * 1000);

      if (parsedData.data.dead.length !== 0) {
        setCurrentGameState(
          `날이 밝았습니다. ${parsedData.data.dead[0]}이 마피아에 의해 살해당했습니다.`
        );
      } else {
        setCurrentGameState(`날이 밝았습니다. 아무도 죽지 않았습니다.`);
      }
    }
    //게임 종료 신호
    else if (parsedData.type === "gameover") {
      setStateTimer(0);
      setCurrentGameState(`${parsedData.data.winner} 승리!`);
      console.log("gameover: ", parsedData.data);
      setRoleList(parsedData.data.roleInfo);

      let b = roleList.filter((user) => user.role === "mafia");
      let a = roleList
        .filter((user) => user.role === "mafia")
        .map((data) => {
          return <div key={data.nickname}>{data.nickname}</div>;
        });
      console.log(b);
      console.log(a);

      handleOpen();
      //게임 종료 후 복귀
    }
  };

  //allcam 컴포에서 상호작용 시 실행될 코드
  const inCamHandler = (res) => {
    if (res.type === "session-connect") {
      client.publish({
        destination: pubGameAddr,
        headers: { token: token, "content-type": "application/json" },
        body: JSON.stringify({
          type: "session-connect",
        }),
        skipContentLengthHeader: true,
      });
    }
    //다른 사람에게 투표 시
    else if (res.type === "vote") {
      client.publish({
        destination: pubGameAddr,
        headers: { token: token, "content-type": "application/json" },
        body: JSON.stringify({
          type: "vote",
          data: {
            target: res.target,
          },
        }),
        skipContentLengthHeader: true,
      });
    } else if (res.type === "night-act") {
      client.publish({
        destination: pubGameAddr,
        headers: { token: token, "content-type": "application/json" },
        body: JSON.stringify({
          type: "vote",
          data: {
            role: res.role,
            target: res.target,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
  };

  client.onConnect = (frame) => {
    console.log("GRP, frame: " + frame);
    // client.subscribe(subAddr, childComponentRef.current.onMessageReceived);
    client.subscribe(subAddr, sideComponentRef.current.onMessageReceived);
    client.subscribe(`${subAddr}/game`, onMessageReceived);
    client.subscribe(`${subAddr}/game/${myNickName}`, onMessageReceived);
  };

  useEffect(() => {
    //소켓 연결 코드는 한번만 랜더링
    client.activate();
    // dispatch(setClient(client));
    let unloadEventLeave = window.addEventListener("unload", () => {
      client.publish({
        destination: pubAddr,
        headers: { token: token, "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "leave",
          },
          data: {
            nickname: myNickName,
          },
        }),
        skipContentLengthHeader: true,
      });
    });
    return () => {
      window.removeEventListener("unload", unloadEventLeave);
    };
  }, []);
  const MafList = () => {
    return roleList
      .filter((user) => user.role === "mafia")
      .map((data) => {
        return <span key={data.nickname}>{data.nickname}</span>;
      });
  };

  return (
    <Container sx={{ height: "100vh", display: "flex", alignItems: "center", minWidth: "1200px" }}>
      <Grid container spacing={3}>
        <Grid item xs={8}>
          <Grid sx={{ marginTop: "15px" }}>
            <Paper elevation={3} sx={{ maxHeight: "120px" }}>
              {/* 좌상 */}
              <button
                onClick={() => {
                  setStateTimer((StateTimer) => 15);
                  console.log("setStateTimer(10)");
                  console.log(stateTimer);
                }}
              ></button>
              <MainBar
                currentGameState={currentGameState}
                myRole={myRole}
                timer={timer}
                stateTimer={stateTimer}
              />
            </Paper>
          </Grid>
          <Grid sx={{ marginTop: "25px" }}>
            {/* 좌하 */}
            <Paper elevation={3} sx={{ minHeight: "650px", maxHeight: "650px" }}>
              {IsGameStart ? (
                <AllCam inCamHandler={inCamHandler} setCurrentGameState={setCurrentGameState} />
              ) : (
                <PlayMap />
              )}
            </Paper>
          </Grid>
        </Grid>
        <Grid item xs={4}>
          <Grid>
            <Paper elevation={3} sx={{ minHeight: "120px" }}>
              {/* 우상 */}
              <NaviBar2
                ref={childComponentRef}
                IsGameStart={IsGameStart}
                setIsGameStart={setIsGameStart}
                client={client}
              />
            </Paper>
          </Grid>
          <Grid sx={{ marginTop: "25px", height: "100%" }}>
            {/* 우하 */}
            <Paper elevation={3} sx={{ minHeight: "650px", maxHeight: "650px" }}>
              <SideBar ref={sideComponentRef} client={client} />
            </Paper>
          </Grid>
        </Grid>
      </Grid>
      {/* 간단 정보 포함 */}
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={style}>
          <Typography id='modal-modal-title' variant='h6' component='h2'>
            {currentGameState}
          </Typography>
          <Typography id='modal-modal-description' sx={{ mt: 2 }}>
            마피아는
            <MafList />
            입니다.
          </Typography>
          {/* 확인 시 모달 닫기 && 세션 종료 && 대기방 복귀 */}
          <Button>확인</Button>
          {/* 중첩 모달창 생성 */}
          <Button>자세히</Button>
        </Box>
      </Modal>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={style}>
          <Typography id='modal-modal-title' variant='h6' component='h2'>
            {currentGameState}
          </Typography>
          <Typography id='modal-modal-description' sx={{ mt: 2 }}>
            마피아는
            <MafList />
            입니다.
          </Typography>
          {/* 확인 시 모달 닫기 && 세션 종료 && 대기방 복귀 */}
          <Button>확인</Button>
          {/* 중첩 모달창 생성 */}
          <Button>자세히</Button>
        </Box>
      </Modal>
    </Container>
  );
};

export default GatherRoom;
