import React, { useState, useEffect } from "react";

//자체 컴포넌트
import MainBar from "../components/GatherRoomPageCom/MainBar";
import NaviBar from "../components/GatherRoomPageCom/NaviBar";
import PlayMap from "../components/GatherRoomPageCom/PlayMap";
import SideBar from "../components/GatherRoomPageCom/SideBar";
import AllCam from "../components/GameRoomPageCom/AllCam";
import { roomNum } from "../redux/slice/RoomNumSlice";
import NaviBar2 from "../components/GatherRoomPageCom/NaviBar2";
//mui 컴포넌트
import Grid from "@mui/material/Grid";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";

//socktjs
import SockJS from "sockjs-client";
var StompJs = require("@stomp/stompjs");

const GatherRoom = () => {
  const subAddr = `/sub/room/${roomNum}`;
  const pubAddr = `/sub/room/${roomNum}`;
  let naviBarVar;
  let NaviBar2Var;

  const parent = (childData) => {
    console.log("childData: ", childData);
    if (childData["num"] === 0) {
      naviBarVar = childData.data;
      console.log("0" + naviBarVar);
    } else if (childData.num === 1) {
      NaviBar2Var = childData.data;
      console.log("1" + NaviBar2Var);
    }

    if (naviBarVar !== undefined && NaviBar2Var !== undefined) {
      client.onConnect(() => {
        client.subscribe(subAddr, naviBarVar.data);
        client.subscribe(subAddr, NaviBar2Var.data);
        console.log("GatherRoom OnCon");
      });
    }
  };

  let subGatherRoom;
  const [IsGameStart, setIsGameStart] = useState(false);
  const client = new StompJs.Client({
    //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
    // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
    connectHeaders: {},
    // debug: function (str) {
    //   console.log(str);
    // },
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  useEffect(() => {
    console.log("gather client: ", client);
    client.activate();

    return () => {};
  }, []);

  client.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };

  return (
    <Container>
      <Grid container spacing={2}>
        <Grid item xs={8}>
          <Grid>
            <Paper elevation={3}>
              <MainBar />
            </Paper>
          </Grid>
          <Grid>
            <Paper elevation={3}>{IsGameStart ? <AllCam /> : <PlayMap />}</Paper>
          </Grid>
        </Grid>
        <Grid item xs={4}>
          <Grid>
            <Paper elevation={3} style={{ height: 50 }}>
              <NaviBar
                IsGameStart={IsGameStart}
                setIsGameStart={setIsGameStart}
                client={client}
                parent={parent}
              />
            </Paper>
          </Grid>
          <Grid>
            <Paper elevation={3}>{/* <SideBar client={client} /> */}</Paper>
          </Grid>
          <Grid>
            <Paper elevation={3} style={{ height: 50 }}>
              <NaviBar2
                IsGameStart={IsGameStart}
                setIsGameStart={setIsGameStart}
                client={client}
                parent={parent}
              />
            </Paper>
          </Grid>
        </Grid>
      </Grid>
    </Container>
  );
};

export default GatherRoom;
