import React, { useState } from "react";
import User from "./User";
import Friends from "../Friends";
import WantedFriends from "../WantedFriends";
import { Paper, Tabs, Tab, Typography, Box } from "@mui/material";
import PropTypes from "prop-types";
// 대기실에 있는 전체 목록, 친구 목록 : 탭

function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role='tabpanel'
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}
TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}
function SideBar({ loginUser, SendF }) {
  const [value, setValue] = useState(0);

  const handleChange = (event, newValue) => {
    console.log(newValue);
    setValue(newValue);
  };

  return (
    <Paper sx={{ width: "100%" }}>
      <Box
        sx={{
          display: "flex",
          justifyContent: "space-evenly",
          borderBottom: 1,
          borderColor: "divider",
          width: "100%",
        }}
      >
        <Tabs value={value} onChange={handleChange} aria-label='basic tabs example'>
          <Tab sx={{ width: "30%" }} label='All' {...a11yProps(0)} />
          <Tab sx={{ width: "30%" }} label='Friends' {...a11yProps(1)} />
          <Tab sx={{ width: "30%" }} label='Requested' {...a11yProps(2)} />
        </Tabs>
      </Box>
      {value === 0 ? <User loginUser={loginUser} SendF={SendF} /> : (value === 1 ? <Friends />: <WantedFriends/>)}
    </Paper>
  );
}

export default SideBar;
