import React, { useState } from "react";
import {
  Table,
  TableContainer,
  TableBody,
  Paper,
  TableRow,
  TableCell,
  Button,
  Menu,
  MenuItem,
} from "@mui/material";
import { useSelector } from "react-redux";
import axios from "axios";
import SearchIcon from "@mui/icons-material/Search";

function User({ loginUser, SendF }) {
  const me = useSelector(state => state.user);
  const friendList = useSelector(state => state.friendList);
  const [ff, setFf] = useState([]);
  const loginUsers = loginUser.filter(e => e.nickname !== me.nickname);

  // 유저 누르면 dropdown
  const [anchorEl, setAnchorEl] = useState(null);
  const [open, setOpen] = useState(0);
  const handleClick = (fri, target) => {
    const friendYesNo = friendList.filter(e => e.nickname === fri.nickname);
    // 내친구인가?
    if (friendYesNo.length !== 0) {
      setOpen(2);
    } else {
      setOpen(1);
    }
    setFf(fri);
    setAnchorEl(target.currentTarget);
  };
  const handleClose = () => {
    setOpen(0);
    setAnchorEl(null);
  };

  const [nameColor, setNameColor] = useState("");
  // 유저 전적
  const userProfile = () => {
    axios({
      method: "post",
      url: "/api/user/findUserByNickname",
      headers: {
        "Content-Type": "text/plain",
        Authorization: `Bearer ${me.accessToken}`,
      },
      data: ff.nickname,
    }).then(res => {
      console.log("rrr", res.data);
      setOpen(0);
      setOpen(3);
      setFf(res.data);
      if (res.data.redUser) {
        setNameColor("red");
      }
    });
  };
  // 유저 검색
  const [search, setSearch] = useState("");
  const [nick, setNick] = useState([]);
  const [searchResult, setSearchResult] = useState("");
  const searchName = e => {
    setSearch(e.target.value);
  };
  // 유저 검색
  const searchUser = () => {
    axios({
      method: "post",
      url: "/api/user/findUserByNicknameElement",
      headers: {
        "Content-Type": "text/plain",
        Authorization: `Bearer ${me.accessToken}`,
      },
      data: search,
    })
      .then(res => {
        // 내 자신은 빼고
        if (res.data.length !== 0) {
          const data = res.data.filter(e => e.nickname !== me.nickname);
          setNick(data);
          setSearchResult(true);
        } else {
          setSearchResult(false);
        }
      })
      .catch(err => {
        setSearchResult(false);
      });
  }
  // 엔터 키 입력
  const handleOnKeyPress = e => {
    if (e.key === "Enter" && search) {
      searchUser()
    }
  };
  const noSearch = () => {
    setSearchResult("");
  };

  return (
    <span>
      <input onChange={searchName} onKeyPress={handleOnKeyPress} />
      {searchResult === "" ? (
        <Button variant='outlined' onClick={()=>{searchUser()}}>
          <SearchIcon />
        </Button>
      ) : (
        <Button variant='outlined' onClick={noSearch}>X</Button>
      )}

      <div>
        {searchResult === "" ? (
          <TableContainer style={{ padding: "20px", height: "100px" }} component={Paper}>
            <Table size='medium'>
              <TableBody>
                {loginUsers &&
                  loginUsers.map((user, index) => (
                    <TableRow key={index}>
                      <TableCell>
                        <Button
                          id='demo-positioned-button'
                          aria-controls={open ? "demo-positioned-menu" : undefined}
                          aria-haspopup='true'
                          aria-expanded={open ? "true" : undefined}
                          onClick={e => handleClick(user, e)}
                        >
                          {user.nickname}
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : searchResult === true ? (
          <TableContainer style={{ padding: "20px", height: "100px" }} component={Paper}>
            <Table size='medium'>
              <TableBody>
                {nick &&
                  nick.map((user, index) => (
                    <TableRow key={index}>
                      <TableCell>
                        <Button
                          id='demo-positioned-button'
                          aria-controls={open ? "demo-positioned-menu" : undefined}
                          aria-haspopup='true'
                          aria-expanded={open ? "true" : undefined}
                          onClick={e => handleClick(user, e)}
                        >
                          {user.nickname}
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <div>"존재하지 않는 닉네임 입니다."</div>
        )}
      </div>
      {/* 친구가 아닌사람 */}
      <Menu
        id='demo-positioned-menu'
        aria-labelledby='demo-positioned-button'
        anchorEl={anchorEl}
        open={open === 1}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
      >
        <MenuItem onClick={userProfile}>전적 보기</MenuItem>
        <MenuItem
          onClick={() => {
            SendF(ff, "offer");
          }}
        >
          친구 신청
        </MenuItem>
      </Menu>
      {/* 친구 */}
      <Menu
        id='demo-positioned-menu'
        aria-labelledby='demo-positioned-button'
        anchorEl={anchorEl}
        open={open === 2}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
      >
        <MenuItem onClick={userProfile}>전적 보기</MenuItem>
      </Menu>
      {/* 전적보기 */}
      <Menu
        id='demo-positioned-menu'
        aria-labelledby='demo-positioned-button'
        anchorEl={anchorEl}
        open={open === 3}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "left",
        }}
      >
        <div style={{ color: nameColor }}>{ff.nickname}</div>
        <br />
        이긴 횟수: {ff.winCount}
        <br />진 횟수: {ff.loseCount}
        <br />
        랭크포인트 : {ff.rankPoint}
      </Menu>
    </span>
  );
}

export default User;
