import React, { useState } from "react";
// import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login, getUser } from "../redux/slice/UserSlice";
import { Container, styleButton, styleModal } from "../style.js";
import { Modal, Box, Button } from "@mui/material";
import { Link, useNavigate, Navigate } from "react-router-dom";
import axios from "axios";
import "../CSS/Login.css";

const SignInPage = (props) => {
  //로그인을 위한 코드
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [wrongInputData, setWrongInputData] = useState(false);
  const [isRedirect] = useState(useSelector((state) => state.user.accessToken));
  const handleWrongInputDataClose = () => {
    setWrongInputData(false);
  };

  const onClickLogin = () => {
    axios({
      method: "post",
      url: "/api/login",
      data: {
        email,
        password,
      },
    })
      .then((response) => {
        dispatch(login({ ...response.data, email }));
        axios({
          method: "get",
          url: "/api/user/me",
          headers: {
            Authorization: `Bearer ` + response.data.accessToken,
          },
        }).then((response2) => {
          dispatch(getUser(response2.data));
          if (response2.data.auth) {
            navigate("/");
          } else {
            navigate("/emailAuth");
          }
        });
      })
      .catch((error) => {
        if (error.response.status === 401) {
          setWrongInputData(true);
        }
      });
  };

  // 회원가입을 위한 코드
  const [modalOpen, setModalOpen] = useState(false);
  const [isValidEmail, setIsValidEmail] = useState("");
  const [signUpEmail, setSignUpEmail] = useState("");
  const [signUpPassword, setSignUpPassword] = useState("");
  const [signUpPasswordCheck, setSignUpPasswordCheck] = useState("");
  const [modalMessage, setModalMessage] = useState("");
  const userSeq = useSelector((state) => state.user.userSeq);

  const goEmailAuthPage = () => {
    if (!isValidEmail) {
      handleModalOpen("이메일 중복검사를 해주세요.");
    } else if (!isSamePassword()) {
      handleModalOpen("비밀번호가 다릅니다.");
    } else {
      userSeq ? navigate("/emailauth") : signup();
    }
  };

  const signup = () => {
    axios({
      method: "post",
      url: "/api/signup",
      headers: {
        "Content-Type": "application/json",
      },
      data: {
        email: signUpEmail,
        password: signUpPassword,
      },
    })
      .then(() => {
        changeForm();
      })
      .catch((error) => {
        if (error.response.status === 401) {
          // setWrongInputData(true);
        }
      });
  };

  const handleModalOpen = (message) => {
    setModalMessage(message);
    setModalOpen(true);
  };
  const handleModalClose = () => {
    setModalOpen(false);
  };

  const isSamePassword = () => {
    return signUpPassword === signUpPasswordCheck;
  };

  const checkEmail = () => {
    axios({
      method: "post",
      url: "/api/checkEmail",
      headers: {
        "Content-Type": "text/plain",
      },
      data: signUpEmail,
    })
      .then((response) => {
        if (response.data) {
          handleModalOpen("이미 사용 중인 이메일입니다.");
        } else {
          setIsValidEmail(true);
          handleModalOpen("사용 가능한 이메일입니다.");
        }
      })
      .catch((error) => {
        if (error.response.status === 401) {
          // setWrongInputData(true);
        }
      });
  };

  //css를 위한 코드
  const [check, setCheck] = useState(true);

  const changeForm = () => {
    setCheck(!check);
  };

  return (
    <Container>
      {isRedirect && <Navigate to='/' />}
      {wrongInputData ? (
        <Modal
          open={wrongInputData}
          onClose={handleWrongInputDataClose}
          aria-labelledby='modal-title'
        >
          <Box sx={{ ...styleModal, width: 400 }}>
            <h2 id='modal-title'>존재하지 않는 이메일이거나 틀린 비밀번호입니다.</h2>
            <Button style={styleButton} onClick={handleWrongInputDataClose}>
              확인
            </Button>
          </Box>
        </Modal>
      ) : null}

      <div className={check ? "cont" : "cont s--signup"}>
        <div className='form sign-in'>
          <h2>Welcome back,</h2>
          <label>
            <span>Email</span>
            <input
              type='email'
              id='email'
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
          </label>
          <label>
            <span>Password</span>
            <input
              type='password'
              id='password'
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
          </label>
          <Link className='forgot-pass' to={`/findpassword`}>
            Forgot password?
          </Link>
          <button type='button' className='submit' onClick={onClickLogin}>
            Sign In
          </button>
          <button type='button' className='fb-btn'>
            Connect with <span>Kakao</span>
          </button>
          <button type='button' className='fb-btn'>
            Connect with <span>Google</span>
          </button>
        </div>

        <Modal open={modalOpen} onClose={handleModalClose} aria-labelledby='modal-title'>
          <Box sx={{ ...styleModal, width: 400 }}>
            <h2 id='modal-title'>{modalMessage}</h2>
            <Button style={styleButton} onClick={handleModalClose}>
              확인
            </Button>
          </Box>
        </Modal>

        <div className='sub-cont'>
          <div className='img'>
            <div className='img__text m--up'>
              <h2>New here?</h2>
              <p>Sign up and discover great amount of new opportunities!</p>
            </div>
            <div className='img__text m--in'>
              <h2>One of us?</h2>
              <p>If you already has an account, just sign in. We've missed you!</p>
            </div>
            <div className='img__btn' onClick={changeForm}>
              <span className='m--up'>Sign Up</span>
              <span className='m--in'>Sign In</span>
            </div>
          </div>
          <div className='form sign-up'>
            <h2>Time to feel like home,</h2>
            <label>
              <span>Email</span>
              <input
                type='text'
                id='signUpEmail'
                onChange={(e) => {
                  setIsValidEmail(false);
                  setSignUpEmail(e.target.value);
                }}
              />
              <Button
                style={styleButton}
                onClick={() => {
                  if (signUpEmail.length) checkEmail();
                  else handleModalOpen("이메일을 입력해주세요.");
                }}
              >
                중복검사
              </Button>
            </label>
            <label>
              <span>Password</span>
              <input
                type='password'
                id='signUpPassword'
                onChange={(e) => {
                  setSignUpPassword(e.target.value);
                }}
              />
            </label>
            <label>
              <span>Check_Password</span>
              <input
                type='password'
                id='signUpPasswordCheck'
                onChange={(e) => {
                  setSignUpPasswordCheck(e.target.value);
                }}
              />
            </label>
            {!isSamePassword() ? <span className='forgot-pass'>비밀번호가 다릅니다.</span> : null}

            <button type='button' className='submit' onClick={goEmailAuthPage}>
              Sign Up
            </button>
            <button type='button' className='fb-btn'>
              Join with <span>Kakao</span>
            </button>
            <button type='button' className='fb-btn'>
              Join with <span>Google</span>
            </button>
          </div>
        </div>
      </div>
    </Container>
  );
};

export default SignInPage;
