import React from "react";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { Button } from "@mui/material";
import styled from "styled-components";
import { styleButton } from "../style.js";
import "../style.js";

const Header = () => {
    const navigate = useNavigate();

    const isAdmin = useSelector(state => state.user.authority) === 'ROLE_ADMIN' ? true : false;
    const token = useSelector(state => state.user.accessToken);

    const goMainPage = () => {
        navigate("/");
    };
    const goSignUpPage = () => {
        navigate("/signup");
    };
    const goSignInPage = () => {
        navigate("/signin");
    };
    const goSignOutPage = () => {
        navigate("/signout");
    };
    const goMyPage = () => {
        navigate("/mypage");
    };
    const goUserListPage = () => {
        navigate("/userlist");
    };

    return (
        <HeaderContainer>
            <Logo onClick={goMainPage}>
                <img src="logo.svg" alt="logo" />
                <p>모두의 마피아</p>
            </Logo>
            <Menu>
                { token ?
                    <>
                        <Button style={styleButton} onClick={goMyPage}>
                            마이페이지
                        </Button>
                        <Button style={styleButton} onClick={goSignOutPage}>
                            로그아웃
                        </Button>
                    </>
                :
                    <>
                        <Button style={styleButton} onClick={goSignInPage}>
                            로그인
                        </Button>
                        <Button style={styleButton} onClick={goSignUpPage}>
                            회원가입
                        </Button>
                    </>
                }
                { isAdmin ?
                    <>
                        <Button style={styleButton} onClick={goUserListPage}>
                            유저 관리
                        </Button>
                    </>
                : null }
            </Menu>
        </HeaderContainer>
    );
};

const HeaderContainer = styled.header`
    position: sticky;
    top: 0px;
    overflow: hidden;
    display: flex;
    flex-direction: row;
    text-align: center;
    align-items: center;
    padding: 0 40px 0 40px;
    height: 60px;
    background-color: var(--color-5);
    justify-content: space-between;
`;

const Logo = styled.a`
    display: flex;
    flex-direction: row;
    cursor: pointer;

    img {
        display: block;
        width: 40px;
    }
    p {
        color: var(--color-2);
        font-size: 20px;
    }
`;

const Menu = styled.section`
    overflow: hidden;
    display: flex;
    flex-direction: row;
    align-items: center;
`;

export default Header;
