import React, { useEffect, useRef, useState } from "react";
import Grid from "@mui/material/Grid";
//의사, 경찰, 마피아수 변경 기능
//
export const GameSetting = () => {
    const [ArrLimit, setArrLimit] = useState([5, 6, 7, 8]);
    const [Limit, setLimit] = useState(0);
    const [Doc, setDoc] = useState();
    let actLimit = [0, 1, 2, 3, 4, 5, 6, 7];
    const [ArrDoc, setArrDoc] = useState([0, 1, 2, 3, 4, 5, 6, 7]);
    const B = useRef();
    const [Cop, setCop] = useState();
    const [ArrCop, setArrCop] = useState([0, 1, 2, 3, 4, 5, 6, 7]);
    const [Maf, setMaf] = useState();
    const [ArrMaf, setArrMaf] = useState([]);

    const tmp = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        // inputFocus.current.focus();
    }, []);

    const SelectBox = (props) => {
        console.log("props.options : ", props.options);
        console.log("props.setNum: ", props.setNum);
        console.log("setLimit: ", setLimit);
        console.log("props.switch: ", props.switch);

        return (
            <select
                onChange={(e) => {
                    props.setNum(e.target.value);
                    let opts = [];
                    for (let i = 0; i <= e.target.value - 1; i++) {
                        opts.push(i);
                    }
                }}
            >
                <option>선택</option>
                {props.options.map((option) => (
                    <option key={option} value={option}>
                        {option}
                    </option>
                ))}
            </select>
        );
    };

    //인원수 선택되었을 시
    const LimitSelected = () => {};
    return (
        <React.Fragment>
            <Grid container spacing={2}>
                <Grid item ws={6}>
                    <Grid>
                        {/* 인원 수 클릭 시 해당  */}
                        <label>인원 수 : </label>
                        <select
                            onChange={(e) => {
                                console.log(e.target.value);
                                setLimit(e.target.value);
                            }}
                        >
                            {ArrLimit.map((data, idx) => {
                                return <option key={idx}>{data}</option>;
                            })}
                        </select>
                        <SelectBox
                            options={ArrLimit}
                            setNum={setLimit}
                            switch={0}
                        />
                        {tmp.current > 0 ? `${tmp.current}명` : ""}
                    </Grid>
                    <Grid>
                        <label>의사 수 : </label>
                        {/* <SelectBox options={Doc} setNum={setDoc} switch={1} /> */}
                        {parseInt(Doc) > 0 ? `${Doc}명` : ""}
                    </Grid>
                    <Grid>
                        <label>경찰 수 : </label>
                        {/* <SelectBox options={actLimit} switch={0} /> */}
                    </Grid>
                    <Grid>
                        <label>마피아 수 : </label>
                        {/* <SelectBox options={actLimit} switch={0} /> */}
                    </Grid>
                </Grid>
                <Grid item ws={6}></Grid>
            </Grid>
            <button>확인</button>
            <button>취소</button>
        </React.Fragment>
    );
};
