import React, { useEffect, useRef, useState } from "react";
import Grid from "@mui/material/Grid";
//의사, 경찰, 마피아 수 검사
export const GameSetting = (props) => {
    let Limit = [{ value: 5 }, { value: 6 }, { value: 7 }, { value: 8 }];
    const [ConNum, setConNum] = useState(0);
    const [Doc, setDoc] = useState([]);
    const [Cop, setCop] = useState([]);
    const [Maf, setMaf] = useState([]);

    const inputFocus = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        // inputFocus.current.focus();
    }, []);

    // function InputLimit() {
    //     return Limit.map((data, idx) => {
    //         return <option key={idx}>{data}</option>;
    //     });
    // }
    //최대 인원 넣기
    const InputLimit = (e) => {
        let newDoc = [];
        for (let i = 0; i <= e.target.value - 1; i++) {
            newDoc += { value: i };
        }
        setDoc((Doc) => {
            return (Doc = newDoc);
        });
        console.log(Doc);
    };

    const SelectBox = (props) => {
        console.log("props.options : ", props.options);

        return (
            <select
                onChange={(e) => {
                    console.log(e.target.value);
                    props.setNum(e.target.value);

                    let A = [];
                    for (let b = 0; b <= e.target.value - 1; b++) {
                        A.push({ value: b });
                    }

                    if (props.switch === 0) {
                        setDoc(A);
                    }
                }}
            >
                <option>선택</option>
                {props.options.map((option) => (
                    <option key={option.value} value={option.value}>
                        {option.value}
                    </option>
                ))}
            </select>
        );
    };

    //인원수 선택되었을 시
    const LimitSelected = () => {};
    return (
        <React.Fragment>
            <Grid container spacing={2}>
                <Grid item ws={6}>
                    <Grid>
                        {/* 인원 수 클릭 시 해당  */}
                        <label>인원 수 : </label>
                        <SelectBox />
                        <p>{ConNum} 명</p>
                    </Grid>
                    <Grid>
                        <label>의사 수 : </label>
                        <SelectBox options={Doc} />
                        {Doc}명
                    </Grid>
                    <Grid>
                        <label>경찰 수 : </label>
                    </Grid>
                    <Grid>
                        <label>마피아 수 : </label>
                    </Grid>
                </Grid>
                <Grid item ws={6}></Grid>
            </Grid>
            <button>확인</button>
            <button>취소</button>
        </React.Fragment>
    );
};
