import React, { useEffect, useState } from "react";
import { OpenVidu } from "openvidu-browser";
import axios from "axios";
import { useSelector } from "react-redux";
import { selectUser } from "../../redux/slice/UserSlice";
import { Grid } from "@mui/material";
import UserVideoComponent from "./UserVideoComponent";
import RoleComboBox from "../RoleComboBox";

//게임 방 번호 가져옴
// import {} from "../../redux/slice/";

export default function AllCam(props) {
  let OV, session, mySession;
  let ovToken;
  const roomNum = useSelector((state) => state.roomNum);
  const currentUser = useSelector(selectUser);

  const [subscribers, setSubscribers] = useState([]);
  const [currentVideoDevice, setCurrentVideoDevice] = useState();
  const [mainStreamManager, setMainStreamManager] = useState();
  const [publisher, setPublisher] = useState();
  const [vote, setVote] = useState(null);
  // let publisher;

  useEffect(() => {
    joinSession();
    window.addEventListener("beforeunload", onbeforeunload);

    return () => {};
  }, []);

  const onbeforeunload = (event) => {
    leaveSession();
  };

  //메인 비디오 컨트롤
  // const handleMainVideoStream = (stream) => {
  //   if (mainStreamManager !== stream) {
  //     mainStreamManager = stream;
  //   }
  // };

  const deleteSubscriber = (streamManager) => {
    let index = subscribers.indexOf(streamManager, 0);
    if (index > -1) {
      setSubscribers([...subscribers].splice(index, 1));
    }
  };

  const joinSession = async () => {
    OV = new OpenVidu();
    session = OV.initSession();
    mySession = session;

    mySession.on("streamCreated", (event) => {
      // Subscribe to the Stream to receive it. Second parameter is undefined
      // so OpenVidu doesn't create an HTML video by its own
      const tmpSubscriber = mySession.subscribe(event.stream, undefined);
      // Update the state with the new subscribers
      setSubscribers((currentSubscribers) => [...currentSubscribers, tmpSubscriber]);
    });

    mySession.on("streamDestroyed", (event) => {
      // Remove the stream from 'subscribers' array
      deleteSubscriber(event.stream.streamManager);
    });

    // On every asynchronous exception...
    mySession.on("exception", (exception) => {
      console.warn(exception);
    });

    await axios({
      url: `/api/room/${roomNum}/video-token`,
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${currentUser.accessToken}`,
      },
    })
      .then(({ data }) => {
        console.log("data: " + data);
        ovToken = data;
      })
      .catch((err) => {
        console.log("get ovToken fail: ", err);
      });

    await mySession
      .connect(ovToken, { clientData: currentUser.nickname })
      .then(async () => {
        props.inCamHandler("session-connect");
        props.setCurrentGameState("세션 접속 완료");
        //모든 입출력 장치 불러옴
        var devices = await OV.getDevices();
        console.log(devices);
        //입출력 장치 중 videoInput == 캠만 배열화 후 저장
        var videoDevices = devices.filter((device) => device.kind === "videoinput");

        // --- 5) Get your own camera stream ---

        // Init a publisher passing undefined as targetElement (we don't want OpenVidu to insert a video
        // element: we will manage it on our own) and with the desired properties

        let tmpPublisher = OV.initPublisher(undefined, {
          audioSource: undefined, // The source of audio. If undefined default microphone
          videoSource: videoDevices[0].deviceId, // The source of video. If undefined default webcam
          publishAudio: false, // Whether you want to start publishing with your audio unmuted or not
          publishVideo: false, // Whether you want to start publishing with your video enabled or not
          resolution: "640x400", // The resolution of your video
          frameRate: 30, // The frame rate of your video
          insertMode: "APPEND", // How the video is inserted in the target element 'video-container'
          mirror: false, // Whether to mirror your local video or not
        });

        // --- 6) Publish your stream ---
        mySession.publish(tmpPublisher);

        // Set the main video in the page to display our webcam and store our Publisher

        setCurrentVideoDevice(videoDevices[0]);
        setMainStreamManager(tmpPublisher);
        setPublisher(tmpPublisher);
      })
      .catch((error) => {
        console.log("There was an error connecting to the session:", error.code, error.message);
      });
  };

  const leaveSession = () => {
    if (mySession) mySession.disconnect();

    OV = null;
    session = undefined;
    subscribers = [];
    setMainStreamManager(undefined);
    setPublisher(undefined);
    // publisher = undefined;
  };

  const switchCamera = async () => {
    try {
      const devices = await OV.getDevices();
      var videoDevices = devices.filter((device) => device.kind === "videoinput");

      if (videoDevices && videoDevices.length > 1) {
        var newVideoDevice = videoDevices.filter(
          (device) => device.deviceId !== currentVideoDevice.deviceId
        );

        if (newVideoDevice.length > 0) {
          // Creating a new publisher with specific videoSource
          // In mobile devices the default and first camera is the front one
          var newPublisher = OV.initPublisher(undefined, {
            videoSource: newVideoDevice[0].deviceId,
            publishAudio: true,
            publishVideo: true,
            mirror: true,
          });

          //newPublisher.once("accessAllowed", () => {
          await session.unpublish(mainStreamManager);

          await session.publish(newPublisher);
          setCurrentVideoDevice(newVideoDevice);
          setMainStreamManager(newPublisher);
          setPublisher(newPublisher);
        }
      }
    } catch (e) {
      console.log(e);
    }
  };

  const handleVote = (index) => {
    if (vote === index) {
      setVote(null);
    } else {
      setVote(index);
    }
  };

  return (
    <div>
      <button>클릭 시 연결</button>
      <Grid container spacing={2} style={{ height: 500 }}>
        <Grid item xs={4}>
          <Grid>
            {/* 1번 자리 내 캠 */}
            {publisher !== undefined ? (
              <UserVideoComponent streamManager={publisher} self={true} />
            ) : null}
          </Grid>
          <Grid>
            {subscribers[2] !== undefined ? (
              <>
                <p>{vote === 2 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(2)}>
                  <UserVideoComponent streamManager={subscribers[2]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
          <Grid>
            {subscribers[4] !== undefined ? (
              <>
                <p>{vote === 4 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(4)}>
                  <UserVideoComponent streamManager={subscribers[4]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
        </Grid>
        <Grid item xs={4}>
          <Grid>
            {subscribers[0] !== undefined ? (
              <>
                <p>{vote === 0 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(0)}>
                  <UserVideoComponent streamManager={subscribers[0]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
          <Grid>empty</Grid>
          <Grid
            onClick={() => {
              handleVote(5);
            }}
          >
            {subscribers[5] !== undefined ? (
              <>
                <p>{vote === 5 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(5)}>
                  <UserVideoComponent streamManager={subscribers[5]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
        </Grid>
        <Grid item xs={4}>
          <Grid>
            {subscribers[1] !== undefined ? (
              <>
                <p>{vote === 1 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(1)}>
                  <UserVideoComponent streamManager={subscribers[1]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
          <Grid>
            {subscribers[3] !== undefined ? (
              <>
                <p>{vote === 3 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(3)}>
                  <UserVideoComponent streamManager={subscribers[3]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
          <Grid>
            {subscribers[6] !== undefined ? (
              <>
                <p>{vote === 6 ? "투표됨" : "_"}</p>
                <div onClick={() => handleVote(6)}>
                  <UserVideoComponent streamManager={subscribers[6]} />
                </div>
                <RoleComboBox />
              </>
            ) : null}
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
}
