import { createSlice } from "@reduxjs/toolkit";

export const CntCopSlice = createSlice({
    name: "CntCopSlice",
    initialState: {
        value: 1,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = CntCopSlice.actions;

export const selectConCnt = (state) => state.cntCop.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default CntCopSlice.reducer;
