import React,{useEffect, useState} from "react";
import { Box, Typography, Grid } from "@mui/material";
import styled from "styled-components";
import {useSelector} from "react-redux"
import axios from "axios";
import { Link, useNavigate } from  "react-router-dom";

const Notice = () => {

    const [noticeList, setNoticeList] = useState([]);
    const token = useSelector(state=>state.user.accessToken)

    useEffect(() => {
        axios.get('/api/board',{
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
          }
        })
        .then(response => {
            setNoticeList(response.data.slice(0,5));
        });
      }, []);


    return(
        <Box sx={{ border: "solid 1px", p: 0.5, width: "80%", m: 2 }}>
            <Typography sx={{ mb: 1 }} align="center">
                공지사항
            </Typography>
            <UL>
                {noticeList.map((notice, index) => {
                    return (
                        <li key={index}>
                            <Grid container>
                                <Grid item xs={3}>{index + 1}</Grid>
                                <Grid item xs={9}>
                                    <Typography sx={{ mb: 1 }} align="left">
                                    <Link to={`/board/${notice.noticeSeq}`}>{notice.title}</Link>
                                    
                                    </Typography>
                                </Grid>
                            </Grid>
                        </li>
                    );
                })}
            </UL>
        </Box>
    );
};
const UL = styled.section`
    list-style: none;
`;
export default Notice;
