import React, { useEffect, useRef, useState } from "react";
import Grid from "@mui/material/Grid";
//의사, 경찰, 마피아수 변경 기능
//
export const GameSetting = () => {
    let Limit = [5, 6, 7, 8];
    const [ConNum, setConNum] = useState(0);
    const [Doc, setDoc] = useState();
    const [Cop, setCop] = useState();
    const [Maf, setMaf] = useState();

    const inputFocus = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        // inputFocus.current.focus();
    }, []);

    const SelectBox = (props) => {
        console.log("props.options : ", props.options);
        console.log("props.setNum: ", props.setNum);
        console.log("props.switch: ", props.switch);

        return (
            <select
                onChange={(e) => {
                    let nextSelectState = [];
                    for (
                        let canSelect = 0;
                        canSelect <= e.target.value - 1;
                        canSelect++
                    ) {
                        nextSelectState.push(canSelect);
                    }

                    if (props.switch === 0) {
                        setDoc(nextSelectState);
                    }
                }}
            >
                <option>선택</option>
                {props.options.map((option) => (
                    <option key={option} value={option}>
                        {option}
                    </option>
                ))}
            </select>
        );
    };

    //인원수 선택되었을 시
    const LimitSelected = () => {};
    return (
        <React.Fragment>
            <Grid container spacing={2}>
                <Grid item ws={6}>
                    <Grid>
                        {/* 인원 수 클릭 시 해당  */}
                        <label>인원 수 : </label>
                        <SelectBox
                            options={Limit}
                            setNum={setConNum}
                            switch={0}
                        />
                        <p>{ConNum} 명</p>
                    </Grid>
                    <Grid>
                        <label>의사 수 : </label>
                        {parseInt(Doc) > 0 ? `${Doc}명` : ""}
                    </Grid>
                    <Grid>
                        <label>경찰 수 : </label>
                    </Grid>
                    <Grid>
                        <label>마피아 수 : </label>
                    </Grid>
                </Grid>
                <Grid item ws={6}></Grid>
            </Grid>
            <button>확인</button>
            <button>취소</button>
        </React.Fragment>
    );
};
