import React, { useState, useEffect, useRef } from "react";
import { Grid } from "@mui/material";
import { OpenVidu } from "openvidu-browser";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { selectUser } from "../../redux/slice/UserSlice";

export default function AllCam() {
  let OV, session, mySession;
  let ovToken;
  let roomNum = 60;
  const userAccessToken = useSelector(selectUser).accessToken;
  joinSession = async () => {
    OV = new OpenVidu();
    session = OV.initSession();
    mySession = session;

    mySession.on("streamCreated", (event) => {
      // Subscribe to the Stream to receive it. Second parameter is undefined
      // so OpenVidu doesn't create an HTML video by its own
      var subscriber = mySession.subscribe(event.stream, undefined);
      var subscribers = this.state.subscribers;
      subscribers.push(subscriber);

      // Update the state with the new subscribers
      this.setState({
        subscribers: subscribers,
      });
    });

    mySession.on("streamDestroyed", (event) => {
      // Remove the stream from 'subscribers' array
      this.deleteSubscriber(event.stream.streamManager);
    });

    // On every asynchronous exception...
    mySession.on("exception", (exception) => {
      console.warn(exception);
    });

    axios({
      url: `https://i7d106.p.ssafy.io:8080/api/room/${roomNum}/video-token`,
      method: "get",
      headers: {
        "Content-Type": "application/json",
        accessToken: `Bearer ${userAccessToken}`,
      },
    });
    mySession.connect("", {});
  };
  return (
    <div></div>
