import { createSlice } from "@reduxjs/toolkit";

export const CntMafSlice = createSlice({
    name: "CntMafSlice",
    initialState: {
        value: 2,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = CntMafSlice.actions;

export const selectConCnt = (state) => state.cntMaf.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default CntMafSlice.reducer;
