import React, { useState, useEffect, useRef } from "react";
import { Grid } from "@mui/material";
import OpenViduSession from "openvidu-react";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { selectUser } from "../../redux/slice/UserSlice";

export default function AllCam() {
  // let Token;
  // let Session;
  const [SessionToken, setSessionToken] = useState();
  const t = useRef();
  const [SessionState, setSessionState] = useState(false);
  const roomNum = 59;
  //axios로 토큰 받아오기
  const user = useSelector(selectUser);
  console.log("user: ", user);

  useEffect(() => {
    axios({
      url: `/api/room/${roomNum}/video-token`,
      method: "get",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${user.accessToken}`,
      },
    })
      .then((res) => {
        console.log(res);
        // setSessionToken(res.data);
        t.current = res.data;
        console.log(t.current);
      })
      .catch((err) => {
        console.log("err: ", err);
      });
  }, []);

  const handlerJoinSessionEvent = () => {
    console.log("Join session");
  };

  const handlerLeaveSessionEvent = () => {
    console.log("Leave session");
    // setSessionState(false);
    // Session = undefined;
  };

  const handlerErrorEvent = () => {
    console.log("Leave session");
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={4}>
        <Grid>
          {t.current ? (
            <OpenViduSession
              id='opv-session'
              sessionName={"이청 테스트 룸0"}
              user={user.nickName}
              token={t.current}
              joinSession={handlerJoinSessionEvent}
              leaveSession={handlerLeaveSessionEvent}
              error={handlerErrorEvent}
            />
          ) : (
            <div>not yet</div>
          )}
        </Grid>
        <Grid>cam4</Grid>
        <Grid>cam6</Grid>
      </Grid>
      <Grid item xs={4}>
        <Grid>cam2</Grid>
        <Grid>empty</Grid>
        <Grid>cam7</Grid>
      </Grid>
      <Grid item xs={4}>
        <Grid>cam3</Grid>
        <Grid>cam5</Grid>
        <Grid>cam8</Grid>
      </Grid>
    </Grid>
  );
}
