import { selectCntCivil } from "../../redux/slice/CntCivilSlice";
import { selectCntCop } from "../../redux/slice/CntCopSlice";
import { selectCntDoc } from "../../redux/slice/CntDocSlice";
import { selectCntLimit } from "../../redux/slice/CntLimitSlice";
import { selectCntMaf } from "../../redux/slice/CntMafSlice";
import { selectRoomTitle } from "../../redux/slice/RoomTitleSlice";

//리로딩되었는지 검사
import { selectChkRL, setCheck } from "../../redux/slice/CheckReloadSlice";
import { selectCntCon, increCon, decreCon, setCon } from "../../redux/slice/CntConSlice";
//방 번호 필요

//socktjs
import SockJS from "sockjs-client";

import { Client, Message } from "@stomp/stompjs";

//memo : 값을 메모이
//callback : 함수를 메모이
export default function PlayMap() {
  //useNavigate 객체 생성
  const navigate = useNavigate();

  //redux 액션 호출
  const dispatch = useDispatch();

  //새로고침되었는지 확인

  let startYX = { y: 650, x: 300 };
  let subGatherRoom = null;
  const myNickName = useSelector(selectUser).nickname;
  const myColor = "#" + Math.round(Math.random() * 0xffffff).toString(16);
  const roooNum = 0;
  const sockADR = `/sub/room/${roooNum}`;

  let seatNum, seatChange, seatCnt, seatNothing;
  const mapSize = 700;
  const charSize = 50;
  const chairSize = 20;
  const table = {
    tbTop: 100,
    tbLeft: 200,
    width: 300,
    height: 300,
  };
  const chairs = [
    [40, 300],
    [40, 400],
    [160, 100],
    [300, 100],
    [450, 300],
    [450, 400],
    [160, 550],
    [300, 550],
  ];

  let beforeReunloadValue = "";

  useBeforeunload((e) => {
    if (beforeReunloadValue !== "AlwaysCheck") {
      e.preventDefault();
    }
  });

  //완전히 페이지를 떠날 때
  window.addEventListener("unload", () => {
    dispatch(setCheck(true));
  });
  //맵 크기
  //포커싱 지정을 위한 변수
  const mapFocus = useRef();
  //div태그를 아바타 객체로 지정
  let myChar;
  //키 입력 시 값 저장 배열
  let keyPress = {},
    pX = useRef(startYX.x),
    pY = useRef(startYX.y),
    speed = 5;
  let otherUserData = {},
    remainNum = { 1: false, 2: false, 3: false, 4: false, 5: false, 6: false, 7: false };
  //웹소켓 선언 및 초기화
  //SockJs + StompJs
  var StompJs = require("@stomp/stompjs");

  const client2 = new StompJs.Client({
    //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
    // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
    connectHeaders: {},
    // debug: function (str) {
    //   console.log(str);
    // },
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  client2.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };

  client2.onConnect = (frame) => {
    //구독하는 곳
    subGatherRoom = client2.subscribe("/sub/room/0", onMessageReceived);
    //유저 방 입장
    userJoin();
    //1초마다
  };

  //소켓통신이 종료되었을 때 실행되는 코드
  client2.onWebSocketClose = () => {};

  //에러 발생시 실행되는 코드
  client2.onStompError = function (frame) {
    // Will be invoked in case of error encountered at Broker
    // Bad login/passcode typically will cause an error
    // Complaint brokers will set `message` header with a brief message. Body may contain details.
    // Compliant brokers will terminate the connection after any error
    console.log("Broker reported error: " + frame.headers["message"]);
    console.log("Additional details: " + frame.body);
  };
  client2.activate();

  //웹 소켓 코드 끝

  const userJoin = () => {
    document.getElementById("myCharacter").style.background = myColor;
    document.getElementById("myCharacter").style.display = "block";
    client2.publish({
      destination: sockADR,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "join",
        },
        data: {
          nickname: myNickName,
          color: myColor,
        },
      }),
      skipContentLengthHeader: true,
    });
  };

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    //내가 송신한 정보는 처리 X
    if (payloadData.data.nickname === myNickName) return;

    //타 유저 정보 처리
    if (payloadData.header.type === "join") {
      for (let i in remainNum) {
        //빈 자리에 넣기
        if (!remainNum[i]) {
          remainNum[i] = true;
          //색변경 => 보이기
          otherUserData[payloadData.data.nickname] = { color: payloadData.data.color, num: i };
          document.getElementById(`char${i}`).style.left = startYX.x;
          document.getElementById(`char${i}`).style.top = startYX.y;

          document.getElementById(`char${i}`).style.background = payloadData.data.color;
          document.getElementById(`char${i}`).style.display = "block";
          document.getElementById(`char${i}Name`).innerText = payloadData.data.nickname;
          break;
        }
      }
    } else if (payloadData.header.type === "leave") {
      let userNum = otherUserData[payloadData.data.nickname].num;
      remainNum[userNum] = false;
      document.getElementById(`char${userNum}`).style.display = "none";
      document.getElementById(`char${userNum}Name`).innerText = "";
    }
    //캐릭터 움직일 때
    else if (payloadData.header.type === "interact") {
      //위치 변경
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.left =
        payloadData.data.x + "px";
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.top =
        payloadData.data.y + "px";
      otherUserData[payloadData.data.nickname]["y"] = payloadData.data.y;
      otherUserData[payloadData.data.nickname]["x"] = payloadData.data.x;

      //의자에 앉았을 때 해당 의자 비활성화
      if (payloadData.data.status === "ready") {
      }
      //의자 일어섰을 때 해당 의자 활성화
      else if (payloadData.data.status === "stand") {
      }
    } else if (payloadData.header.type === "start") {
      console.log(payloadData.data);
    } else if (payloadData.header.type === "setting") {
      console.log(payloadData.data);
    }
  };

  //웹소켓 코드 끝

  //렌더링될 때마다 myChar가 #myCharacter 를 가르킴
  useEffect(() => {
    let reloadValue = useSelector(selectUser);
    // console.log(reloadValue);
    // if (reloadValue) {
    //   console.log("isTrue?");
    //   dispatch(setCheck(!reloadValue));
    //   navigate("/");
    // }
    myChar = document.getElementById("myCharacter");
    console.log("myChar rendered");
    //랜더링 종료 시 인터벌 종료
    return () => {
      clearInterval(inputCheck);

      //랜더링 종료될 때 leave 신호 전송
      console.log("render returned");
      client2.publish({
        destination: "/sub/room/0",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "leave",
          },
          data: {
            nickname: myNickName,
          },
        }),

        skipContentLengthHeader: true,
      });
      //랜더링이 종료될 때 소켓 통신 종료
      client2.deactivate();
    };
  });

  //약 60fps로 위치 정보 전송, 움직일 때만 전송
  const sendMyPos = setInterval(() => {
    if (client2) {
      if (keyPress[87] || keyPress[83] || keyPress[65] || keyPress[68]) {
        client2.publish({
          destination: sockADR,
          headers: { "content-type": "application/json" },
          body: JSON.stringify({
            header: {
              type: "interact",
            },
            data: {
              nickname: myNickName,
              status: "move",
              chairNum: null,
              x: pX.current,
              y: pY.current,
            },
          }),
          skipContentLengthHeader: true,
        });
      }
    }
  }, 16);

  //부드러운 움직임을 위한 변수 선언
  //일정 초마다 키 입력 확인
  const inputCheck = setInterval(() => {
    if (keyPress[87]) pY.current -= speed;
    if (keyPress[83]) pY.current += speed; // down - s
    if (keyPress[65]) pX.current -= speed; // left - a
    if (keyPress[68]) pX.current += speed;

    //테이블 충돌 감지
    if (
      pX.current < table.tbLeft + table.width &&
      pX.current + charSize > table.tbLeft &&
      pY.current < table.tbTop + table.height &&
      charSize + pY.current > table.tbTop
    ) {
      if (keyPress[87]) pY.current += speed;
      if (keyPress[83]) pY.current -= speed; // down - s
      if (keyPress[65]) pX.current += speed; // left - a
      if (keyPress[68]) pX.current -= speed;
    }

    seatNum = -1;
    //의자 충돌 감지
    for (let i = 0; i < 8; i++) {
      if (
        chairs[i][1] < pX.current + charSize &&
        chairs[i][1] + chairSize > pX.current &&
        chairs[i][0] < charSize + pY.current &&
        chairs[i][0] + chairSize > pY.current
      ) {
        seatNum = i;
        //자리에 앉았을 시 소켓으로 준비 완료 전송
        if (seatNum !== seatChange) {
          seatNothing = true;
          document.getElementById("inputTag").value = "준비 완료";
          console.log("SeatNum: ", seatNum);
          console.log("send ready");
          seatChange = i;

          client2.publish({
            destination: "/sub/room/0",
            headers: { "content-type": "application/json" },
            body: JSON.stringify({
              header: {
                type: "interact",
              },
              data: {
                nickname: myNickName,
                status: "ready",
                chairNum: seatChange,
                y: pY.current,
                x: pX.current,
              },
            }),
            skipContentLengthHeader: true,
          });
        }
        //자리와 이전 자리가 같을 시
      }
    }
    //자리에서 벗어났을 시 소켓으로 준비 완료 해제 전송
    if (seatNum === -1 && seatNothing === true) {
      console.log("send not ready");
      document.getElementById("inputTag").value = "준비 중";
      seatCnt = 0;
      seatNothing = false;

      client2.publish({
        destination: "/sub/room/0",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "interact",
          },
          data: {
            nickname: myNickName,
            status: "stand",
            chairNum: -1,
            y: pY.current,
            x: pX.current,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
    if (pY.current < 0)
      //외부 벽 충돌 감지
      pY.current = 0;
    if (pY.current >= mapSize - charSize) pY.current = mapSize - charSize;
    if (pX.current < 0) pX.current = 0;
    if (pX.current >= mapSize - charSize) pX.current = mapSize - charSize;

    //캐릭터 좌표 최신화
    myChar.style.left = pX.current + "px";
    myChar.style.top = pY.current + "px";
  }, 16);

  //첫 렌더링될 때만 입력받는 input태그로 focus
  useEffect(() => {
    console.log("useEffect focus once");
    mapFocus.current.focus();
  }, []);

  //맵 클릭 시 입력 포커싱 지정
  const changeFocus = () => {
    mapFocus.current.focus();
  };

  //키 눌렀을 때 해당 값 true로 변환
  const handleKeyDown = (e) => {
    keyPress[e.keyCode.toString()] = true;
  };
  //키 땠을 때 해당 값 false로 변환
  const handleKeyUp = (e) => {
    if (keyPress[87] || keyPress[83] || keyPress[65] || keyPress[68]) {
      client2.publish({
        destination: sockADR,
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "interact2",
          },
          data: {
            nickname: myNickName,
            status: "move",
            chairNum: null,
            x: pX.current,
            y: pY.current,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
    keyPress[e.keyCode.toString()] = false;
  };

  //중간에 게임방을 나갈 때 진짜 나갈 것인지 확인 받기
  const [Value, setValue] = useState("");
  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        height: "100%",
      }}
    >
      <input
        id='inputTag'
        onChange={(e) => setValue(e.target.value)}
        value='준비 중'
        type='text'
        ref={mapFocus}
        onKeyDown={handleKeyDown}
        onKeyUp={handleKeyUp}
        readOnly
        style={{
          border: "none",
          cursor: "default",
          margin: "0",
          position: "absolute",
          top: 5,
          left: 5,
        }}
      />
      <div
        id='map'
        onClick={changeFocus}
        style={{
          width: mapSize,
          height: mapSize,
          background: "orange",
          position: "relative",
          left: 0,
          top: 0,
        }}
      >
        <div
          id='table'
          style={{
            width: table.width,
            height: table.height,
            left: table.tbLeft,
            top: table.tbTop,
            background: "green",
            position: "absolute",
          }}
        ></div>

        {chairs.map((e, idx) => {
          return (
            <div
              key={idx}
              id={"chair" + idx}
              style={{
                top: e[0],
                left: e[1],
                height: chairSize,
                width: chairSize,
                background: "blue",
                position: "absolute",
              }}
            ></div>
          );
        })}
        <div
          id='myCharacter'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            position: "absolute",
            display: "none",
          }}
        >
          <div style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}>
            {myNickName}
          </div>
        </div>
        {/* 다른 유저의 캐릭터 작성 */}

        <div
          id='char1'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char1Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char2'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char2Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char3'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char3Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char4'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char4Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char5'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char5Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char6'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char6Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char7'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char7Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
      </div>
    </div>
  );
}
