import React, { useState } from "react";
// import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login, getUser } from "../redux/slice/UserSlice";
import { Container, styleButton, styleModal, styleTextField } from '../style.js';
import { Modal, Box, Button, TextField, Card, CardContent } from '@mui/material';
import { Link, useNavigate, Navigate } from  "react-router-dom";
import styled from 'styled-components';
import axios from 'axios';


const SignInPage = (props) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [wrongInputData, setWrongInputData] = useState(false);
  const [isRedirect] = useState(useSelector(state => state.user.accessToken));

  const handleWrongInputDataClose = () => {
    setWrongInputData(false);
  };

  const onClickLogin = () => {
    axios({
      method: 'post',
      url: '/api/login',
      data: {
        email,
        password,
      }
    })
    .then(response => {
      dispatch(login({...response.data, email}));
      axios({
        method: 'get',
        url: '/api/user/me',
        headers: {
            Authorization: `Bearer ` + response.data.accessToken,
        }
      })
      .then(response2 => {
        dispatch(getUser(response2.data));
        if (response2.data.auth) {
          navigate("/");
        } else {
          navigate("/emailAuth");
        }
      })
    })
    .catch(error => {
      if (error.response.status === 401) {
        setWrongInputData(true);
      }
    })
    
  }
  
  return (
    
    <Container>
      { isRedirect && <Navigate to="/"/> }
      { wrongInputData ?
          <Modal
            open={wrongInputData}
            onClose={handleWrongInputDataClose}
            aria-labelledby="modal-title"
          >
            <Box sx={{ ...styleModal, width: 400 }}>
              <h2 id="modal-title">존재하지 않는 이메일이거나 틀린 비밀번호입니다.</h2>
              <Button style={styleButton} onClick={handleWrongInputDataClose}>확인</Button>
            </Box>
          </Modal>
      : null }



      <Card
      sx={{height: "300",
      width:"15%",
      
      }}>

        <CardContent>
          
          <h2>로그인</h2>
          <Box
          sx={{ display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center', }}
          >
            <Content>
              <Box
                sx={{
                  width: 300,
                  maxWidth: '100%',
                }}
              >
                <TextField fullWidth style={styleTextField} name="email" placeholder="E-mail" onChange={(e) => {setEmail(e.target.value)}}/>
              </Box>
            </Content>
            <Content>
              <Box
                  sx={{
                    width: 300,
                    maxWidth: '100%',
                  }}
                >
                <TextField fullWidth style={styleTextField} type="password" name="password" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}}/>
              </Box>
            </Content>
          </Box>

          <Box 
          sx={{
            display: 'flex',
            justifyContent: 'space-between',
            p: 1,
            m: 1,
            bgcolor: 'background.paper',
            borderRadius: 1,
          }}
          >
            <Link to={`/signup`}>회원가입</Link>
            
            <Link to={`/findpassword`}>비밀번호 찾기</Link>
          </Box>

          <Button variant="contained" color="success" disableElevation onClick={onClickLogin}>E-mail 로그인</Button>
          <br/>
          <br/>
          <Button variant="contained" disableElevation color="warning">Kakao 로그인</Button>
          <br/>
          <br/>
          <Button variant="contained" color="error" disableElevation >Google 로그인</Button>
        </CardContent>
      </Card>
    </Container>
  );
};

const Content = styled.div`
  overflow: hidden;
  display: block;
  text-align: center;
`

export default SignInPage;
