import React, { useState, useEffect } from "react";
import MainBar from "../components/GatherRoomCom/MainBar";
import NaviBar from "../components/GatherRoomCom/NaviBar";
import PlayMap from "../components/GatherRoomCom/PlayMap";
import SideBar from "../components/GatherRoomCom/SideBar";
import Grid from "@mui/material/Grid";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";
import AllCam from "../components/GameRoomCom/AllCam";
export function GatherRoom() {
    //방 접속 시 방의 정보를 받아 저장
    //방 번호
    // const [roomNum, setRoomNum] = useState(props.roomNum);
    // //방 제목
    // const [roomName, setRoomName] = useState(props.roomName);
    // //현재 접속 인원
    // const [crntCon, setCrntCon] = useState(props.crntCon);
    // //최대 접속 가능 인원
    // const [maxCon, setmaxCon] = useState(props.maxCon);
    //
    const [start, setStart] = useState(true);

    function IsGameStart(props) {
        if (props.start) return <PlayMap />;
        else return <AllCam />;
    }

    return (
        <Container>
            <Grid container spacing={2}>
                <Grid item xs={8}>
                    {/* 따로 xs 크기를 지정해주지 않으면 row*/}
                    <Grid>
                        <Paper elevation={3}>
                            <MainBar />
                        </Paper>
                    </Grid>
                    <Grid>
                        <Paper elevation={3}>
                            <IsGameStart start={start} />
                        </Paper>
                    </Grid>
                </Grid>
                <Grid item xs={4}>
                    <Grid>
                        <Paper elevation={3} style={{ height: 50 }}>
                            <NaviBar crntStart={start} onClick={setStart} />
                        </Paper>
                    </Grid>
                    <Grid>
                        <Paper elevation={3}>
                            <SideBar />
                        </Paper>
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    );
}

export default GatherRoom;
