import React, { useEffect, useState } from "react";

//자체 컴포넌트
import MainBar from "../components/GatherRoomPageCom/MainBar";
import NaviBar from "../components/GatherRoomPageCom/NaviBar";
import PlayMap from "../components/GatherRoomPageCom/PlayMap";
import SideBar from "../components/GatherRoomPageCom/SideBar";
import AllCam from "../components/GameRoomPageCom/AllCam";

//mui 컴포넌트
import Grid from "@mui/material/Grid";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";

//socktjs
import SockJS from "sockjs-client";

const GatherRoom = () => {
  //현재 입장한 방 번호
  const roooNum = 0;
  let subGatherRoom = "";
  //구독할 주소
  const sockADR = `/sub/room/${roooNum}`;

  //웹소켓 선언 및 초기화
  //SockJs + StompJs
  var StompJs = require("@stomp/stompjs");

  const client = new StompJs.Client({
    //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
    // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
    connectHeaders: {},
    // debug: function (str) {
    //   console.log(str);
    // },
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  client.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };

  //소켓통신이 종료되었을 때 실행되는 코드
  client.onWebSocketClose = () => {
    console.log("socket close");
  };

  //에러 발생시 실행되는 코드
  client.onStompError = function (frame) {
    // Will be invoked in case of error encountered at Broker
    // Bad login/passcode typically will cause an error
    // Complaint brokers will set `message` header with a brief message. Body may contain details.
    // Compliant brokers will terminate the connection after any error
    console.log("Broker reported error: " + frame.headers["message"]);
    console.log("Additional details: " + frame.body);
  };

  const [IsGameStart, setIsGameStart] = useState(false);

  client.onConnect = (frame) => {
    console.log("run onConnect");
    //구독 및 연결 시 메세지 전송
    console.log("onMessageReceived: ", onMessageReceived);
    subGatherRoom = client.subscribe("/sub/room/0", onMessageReceived);
    joinUser();
    console.log("ended onConnect");
  };

  const onMessageReceived = (payload) => {
    let data = JSON.parse(payload);
    console.log(data);
  };

  const joinUser = () => {
    client.publish({
      destination: sockADR,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "list",
        },
        data: {
          nickname: myNickName,
          color: myColor,
          x: pX.current,
          y: pY.current,
        },
      }),
      skipContentLengthHeader: true,
    });
  };

  //한번만 실행
  useEffect(() => {
    console.log("run client.activate");
    client.activate();
    console.log("exit client.activate");

    //랜더링 종료될 때 실행
    return () => {
      console.log("run client.deactivate");
      client.deactivate();
    };
  }, []);

  return (
    <Container>
      <Grid container spacing={2}>
        <Grid item xs={8}>
          <Grid>
            <Paper elevation={3}>
              <MainBar />
            </Paper>
          </Grid>
          <Grid>
            <Paper elevation={3}>
              {
                IsGameStart ? <AllCam sockADR={sockADR} client2={client} /> : null
                // <PlayMap
                // sockADR={sockADR}
                // client2={client2}
                // setOnMessageReceived={setOnMessageReceived}
                // subGatherRoom={subGatherRoom}
                // setUserJoin={setUserJoin}
                // />
              }
            </Paper>
          </Grid>
        </Grid>
        <Grid item xs={4}>
          <Grid>
            <Paper elevation={3} style={{ height: 50 }}>
              <NaviBar
                sockADR={sockADR}
                IsGameStart={IsGameStart}
                setIsGameStart={setIsGameStart}
              />
            </Paper>
          </Grid>
          <Grid>
            <Paper elevation={3}>
              <SideBar client2={client} />
            </Paper>
          </Grid>
        </Grid>
      </Grid>
    </Container>
  );
};

export default GatherRoom;
