import React, { useState } from "react";

//자체 컴포넌트
import MainBar from "../components/GatherRoomPageCom/MainBar";
import NaviBar from "../components/GatherRoomPageCom/NaviBar";
import PlayMap from "../components/GatherRoomPageCom/PlayMap";
import SideBar from "../components/GatherRoomPageCom/SideBar";
import AllCam from "../components/GameRoomPageCom/AllCam";

//mui 컴포넌트
import Grid from "@mui/material/Grid";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";

//socktjs
import { over } from "stompjs";
import SockJS from "sockjs-client";

export function GatherRoom() {
    const [IsGameStart, setIsGameStart] = useState(false);

    const GameStart = () => {
        if (IsGameStart) {
            return <AllCam />;
        } else return <PlayMap />;
    };

    //웹소켓 코드 시작
    var stompClient = null;

    const [userData, setUserData] = useState({
        username: "jik",
        connected: false,
        message: "",
    });

    const connect = () => {
        console.log("function connect");
        let Sock = new SockJS("https://i7d106.p.ssafy.io:8080/ws");
        console.log("let Sock: ", Sock);
        stompClient = over(Sock);
        console.log("let stompClient: ", stompClient);
        stompClient.connect({}, onConnected, onError);
    };

    const onConnected = () => {
        console.log("function onConnected");
        setUserData({ ...userData, connected: true });
        stompClient.subscribe("/ws/pub/room/0", onMessageReceived);

        // stompClient.subscribe(
        //     "/user/" + userData.username + "/private",
        //     onPrivateMessage
        // );
        // userJoin();
    };

    const onError = (err) => {
        console.log("function onError");
        console.log(err);
    };

    const onMessageReceived = (payload) => {
        console.log("function onMessageReceived");
        var payloadData = JSON.parse(payload.body);
        console.log(payloadData);
    };
    const handleMessage = (event) => {
        console.log("function handleMessage");
        const { value } = event.target;
        setUserData({ ...userData, message: value });
    };
    const sendValue = () => {
        if (stompClient) {
            var chatMessage = {
                senderName: userData.username,
                message: userData.message,
                status: "MESSAGE",
            };
            console.log(chatMessage);
            stompClient.send("/app/message", {}, JSON.stringify(chatMessage));
            setUserData({ ...userData, message: "" });
        }
    };

    connect();
    //웹소켓 코드 끝

    return (
        <Container>
            <Grid container spacing={2}>
                <Grid item xs={8}>
                    {/* 따로 xs 크기를 지정해주지 않으면 row*/}
                    <Grid>
                        <Paper elevation={3}>
                            <input type="text" onChange={handleMessage} />
                            <MainBar />
                        </Paper>
                    </Grid>
                    <Grid>
                        <Paper elevation={3}>
                            <GameStart />
                        </Paper>
                    </Grid>
                </Grid>
                <Grid item xs={4}>
                    <Grid>
                        <Paper elevation={3} style={{ height: 50 }}>
                            <NaviBar
                                IsGameStart={IsGameStart}
                                setIsGameStart={setIsGameStart}
                            />
                        </Paper>
                    </Grid>
                    <Grid>
                        <Paper elevation={3}>
                            스톰프 테스트
                            <SideBar />
                        </Paper>
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    );
}

export default GatherRoom;
