import React, { useState, useEffect } from "react";
import Nav from "../components/RoomListPageCom/Nav";
import SideBar from "../components/RoomListPageCom/SideBar";
import RoomList from "../components/RoomListPageCom/RoomList";
import MyProfile from "../components/RoomListPageCom/MyProfile";
import Chat from "../components/RoomListPageCom/Chat";
import { Container, Stack } from "@mui/material";

import SockJS from "sockjs-client";
import { over } from "stompjs";

import { useSelector} from "react-redux";

var stompClient = null;
function RoomListPage() {
    const me = useSelector((state) => state.user);
    console.log(me)
    // 채팅 관련 데이터
    const [Chats, setChats] = useState([]); // 채팅 목록
    // 로그인 유저 목록
    const [loginUser, setLoginUser] = useState([]);

    // roomListPage 들어오면 연결
    useEffect(() => {
        connect();
    }, []);
    const connect = () => {
        let Sock = new SockJS("https://i7d106.p.ssafy.io:8080/ws");
        stompClient = over(Sock);
        stompClient.connect({}, onConnected, onError);
    };
    // 구독 연결
    const onConnected = () => {
        console.log("구독 연결");
        stompClient.subscribe("/sub/lobby", Received);
        console.log("저 로비에 있어요!!", me.nickname);
        const data = {
            header:{
                type:"join",
            },
            data:{
                token: me.accessToken,
                nickname:me.nickname,
            }
        }
        stompClient.send("/pub/lobby", {}, JSON.stringify(data));
    };

    const onError = (err) => {
        console.log(err);
    };
    // 서버로부터 받기
    const Received = (payload) => {
        var payloadData = JSON.parse(payload.body); // JSON 문자열을 JavaScript 객체로 변환
        console.log("서버가 준거", payloadData); 
        if (payloadData.header.type === "chat") {
            Chats.push(payloadData);
            setChats([...Chats]);
        } else {
            if (payloadData.header.type === "join") {
                console.log('서버가 주는 로그인된 유저 목록', payloadData)
                setLoginUser(payloadData.data.users);
            }
        }
    };

    // 서버로 전송하기 (내가 쓴 채팅 전송)
    const Send = (data) => {
        console.log(stompClient);
        // waitForConnection(stompClient,function() {
        //     stompClient.send("/pub/messages", {type: "chat"}, JSON.stringify(data));
        //     console.log('채팅 보냈습니다.')
        // })
        if (stompClient.ws.readyState === 1) {
            console.log("보낸다");
            return stompClient.send("/pub/lobby", {}, JSON.stringify(data));
        }
    };

    return (
        <div>
            <Container maxWidth="lg" sx={{ mt: 1 }}>
                <Stack
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                    spacing={1}
                >
                    <Nav />
                    <Stack
                        direction="row"
                        justifyContent="center"
                        alignItems="center"
                        spacing={1}
                    >
                        <Stack
                            direction="column"
                            justifyContent="center"
                            alignItems="center"
                            spacing={1}
                        >
                            <SideBar loginUser={loginUser} />
                            <MyProfile />
                        </Stack>
                        <Stack
                            direction="column"
                            justifyContent="center"
                            alignItems="center"
                            spacing={1}
                        >
                            <RoomList />
                            <Chat Chats={Chats} Send={Send} />
                        </Stack>
                    </Stack>
                </Stack>
            </Container>
        </div>
    );
}
export default RoomListPage;
