import React, { useRef, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import { selectColor, selectX, selectY, setColor, setX, setY } from "../../redux/slice/CharSlice";
import { selectUser } from "../../redux/slice/UserSlice";
// import { useBeforeunload } from "react-beforeunload";

//라우터 이동 위한 클래스
// import { useNavigate } from "react-router-dom";
//현재 방 정보 업데이트 함수
// import { selectCntCivil, setCivil } from "../../redux/slice/CntCivilSlice";
import { setCop } from "../../redux/slice/CntCopSlice";
import { setDoc } from "../../redux/slice/CntDocSlice";
import { setLimit } from "../../redux/slice/CntLimitSlice";
import { setMaf } from "../../redux/slice/CntMafSlice";
// import { selectRoomTitle, RoomTitleSlice } from "../../redux/slice/RoomTitleSlice";
import { setNightTime, setTalkTime, setVoteTime } from "../../redux/slice/GameTimeSlice";
//리로딩되었는지 검사
// import { selectChkRL, setChkRL } from "../../redux/slice/CheckReloadSlice";
// import { selectCntCon, increCon, decreCon, setCon } from "../../redux/slice/CntConSlice";
//방 번호 필요

//memo : 값을 메모이
//callback : 함수를 메모이
export default function PlayMap(props) {
  //useNavigate 객체 생성
  // const navigate = useNavigate();

  //redux 액션 호출
  const dispatch = useDispatch();

  //리로드 확인
  // useBeforeunload((event) => {
  //   if (true) {
  //     event.preventDefault();
  //     dispatch(SavePosX(pX.current));
  //     dispatch(SavePosY(pY.current));
  //   }
  // });

  //시작 지점
  let startYX = { y: 600, x: 300 };

  //소켓 연결 시 구독한 주소 반환 저장
  let subGatherRoom = null;

  //내 아이디
  const myNickName = useSelector(selectUser).nickname;

  //처음 랜더링될 때 내 색을 지정

  let myColor = useSelector(selectColor);
  if (myColor == undefined) {
    myColor = "#" + Math.round(Math.random() * 0xffffff).toString(16);
    dispatch(setColor(myColor));
  }

  let seatNum, seatChange, seatCnt, seatNothing;

  //게임 맵 크기
  const mapSize = 700;

  //맵 출력 테이블 위치, 사이즈
  const table = {
    tbTop: 100,
    tbLeft: 200,
    width: 300,
    height: 300,
  };

  //맵에 출력할 의자 위치 (top, left)
  const chairs = [
    [40, 300],
    [40, 400],
    [160, 100],
    [300, 100],
    [450, 300],
    [450, 400],
    [160, 550],
    [300, 550],
  ];
  const charSize = 50;
  const chairSize = 20;
  let chairSeatState = { 1: false, 2: false, 3: false, 4: false, 5: false, 6: false, 7: false };
  //포커싱 지정을 위한 변수
  const mapFocus = useRef();

  //div태그를 아바타 객체로 지정
  let myChar = document.getElementById("myCharacter");

  //키 입력 시 값 저장 배열, 움직였을 때 위치 저장, 속도
  let keyPress = {},
    pX = useRef(useSelector(selectX)),
    pY = useRef(useSelector(selectY)),
    speed = 5;
  console.log(pX.current);
  console.log(pY.current);

  //타 유저의 데이터 저장
  let otherUserData = {},
    //현재 몇번에 유저가 저장되어있는지 확인
    remainNum = { 1: false, 2: false, 3: false, 4: false, 5: false, 6: false, 7: false };

  const sockADR = props.sockADR;
  const client2 = props.client2;
  // //웹소켓 선언 및 초기화
  // //SockJs + StompJs
  // var StompJs = require("@stomp/stompjs");

  // const client2 = new StompJs.Client({
  //   //websocket 주소만 입력 가능 * ws://, wss:// 로 시작
  //   // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
  //   connectHeaders: {},
  //   // debug: function (str) {
  //   //   console.log(str);
  //   // },
  //   reconnectDelay: 5000,
  //   heartbeatIncoming: 4000,
  //   heartbeatOutgoing: 4000,
  // });

  // client2.webSocketFactory = () => {
  //   return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  // };

  // client2.onConnect = frame => {
  //   console.log("client2.onConnected start");
  //   //구독하는 곳
  //   subGatherRoom = client2.subscribe("/sub/room/0", onMessageReceived);
  //   userJoin();
  //   console.log("client2.onConnected ended");
  // };

  // //소켓통신이 종료되었을 때 실행되는 코드
  // client2.onWebSocketClose = () => {
  //   console.log("socket close");
  // };

  // //에러 발생시 실행되는 코드
  // client2.onStompError = function(frame) {
  //   // Will be invoked in case of error encountered at Broker
  //   // Bad login/passcode typically will cause an error
  //   // Complaint brokers will set `message` header with a brief message. Body may contain details.
  //   // Compliant brokers will terminate the connection after any error
  //   console.log("Broker reported error: " + frame.headers["message"]);
  //   console.log("Additional details: " + frame.body);
  // };

  // //웹 소켓 코드 끝
  // client2.activate();

  //웹 소켓 코드 끝

  //**************************************** */
  //** 소켓으로 정보를 받았을 때 처리 함수 **
  //**************************************** */
  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    console.log(payloadData);
    //내가 송신한 정보는 처리 X
    if (payloadData.data.nickname === myNickName) return;

    //타 유저 정보 처리
    //방 접속 신호 수신
    if (payloadData.header.type === "join") {
      console.log(payloadData);
      //이미 방에 접속해있는 유저인 경우 처리해주지 않음
      if (otherUserData[payloadData.data.nickname]) return;

      //새로 들어왔으면 해당 유저에게 자신의 정보를 반환
      client2.publish({
        destination: sockADR,
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "list",
          },
          data: {
            nickname: myNickName,
            color: myColor,
            x: pX.current,
            y: pY.current,
          },
        }),
        skipContentLengthHeader: true,
      });

      //새로 접속해온 경우 남아있는 자리를 확인
      for (let i in remainNum) {
        //빈 자리에 넣기
        if (!remainNum[i]) {
          remainNum[i] = true;
          //색변경 => 보이기
          otherUserData[payloadData.data.nickname] = { color: payloadData.data.color, num: i };
          document.getElementById(`char${i}`).style.left = startYX.x;
          document.getElementById(`char${i}`).style.top = startYX.y;
          document.getElementById(`char${i}`).style.background = payloadData.data.color;
          document.getElementById(`char${i}`).style.display = "block";
          document.getElementById(`char${i}Name`).innerText = payloadData.data.nickname;
          break;
        }
      }
    }
    //join 정보에
    else if (payloadData.header.type === "list") {
      console.log("list: ", payloadData);
      if (otherUserData[payloadData.data.nickname]) return;

      //새로 접속해온 경우 남아있는 자리를 확인
      for (let i in remainNum) {
        //빈 자리에 넣기
        if (!remainNum[i]) {
          remainNum[i] = true;
          //색변경 => 보이기
          otherUserData[payloadData.data.nickname] = { color: payloadData.data.color, num: i };
          document.getElementById(`char${i}`).style.left = payloadData.data.x + "px";
          document.getElementById(`char${i}`).style.top = payloadData.data.y + "px";
          document.getElementById(`char${i}`).style.background = payloadData.data.color;
          document.getElementById(`char${i}`).style.display = "block";
          document.getElementById(`char${i}Name`).innerText = payloadData.data.nickname;
          break;
        }
      }
    }
    //방을 떠나는 신호 수신
    else if (payloadData.header.type === "leave") {
      console.log("type: ", payloadData.header.type + ", body: " + payloadData.body);
      let userNum = otherUserData[payloadData.data.nickname].num;
      remainNum[userNum] = false;
      otherUserData[payloadData.data.nickname] = undefined;
      document.getElementById(`char${userNum}`).style.display = "none";
      document.getElementById(`char${userNum}Name`).innerText = "";
    }
    //캐릭터 움직일 때
    else if (payloadData.header.type === "interact") {
      if (otherUserData[payloadData.data.nickname] === undefined) {
        console.log("undefined error, otherUserData: ", otherUserData);
      }
      //위치 변경
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.left =
        payloadData.data.x + "px";
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.top =
        payloadData.data.y + "px";
      otherUserData[payloadData.data.nickname]["y"] = payloadData.data.y;
      otherUserData[payloadData.data.nickname]["x"] = payloadData.data.x;

      //의자에 앉았을 때 해당 의자 비활성화
      if (payloadData.data.status === "ready") {
        console.log(
          "레디 신호 수신, 이름 의자번호: " +
            payloadData.data.nickname +
            " " +
            payloadData.data.chairNum
        );
      }
      //의자 일어섰을 때 해당 의자 활성화
      else if (payloadData.data.status === "stand") {
      }
    } else if (payloadData.header.type === "start") {
      console.log(payloadData.data);
    }
    //게임 설정 변경 데이터 수신
    else if (payloadData.header.type === "setting") {
      console.log("type: ", payloadData.header.type + ", body: " + payloadData.body);
      //방 제목 변경 추후
      dispatch(setLimit(payloadData.data.capacity));
      dispatch(setMaf(payloadData.data.mafia));
      dispatch(setDoc(payloadData.data.doctor));
      dispatch(setCop(payloadData.data.police));
      dispatch(setMaf(payloadData.data.mafia));
      dispatch(setTalkTime(payloadData.data.talkTime));
      dispatch(setVoteTime(payloadData.data.voteTime));
      dispatch(setNightTime(payloadData.data.nightTime));
    }
    //해당 되지 않는 타입 출력
    else {
      console.log("else: ", payloadData);
    }
  };
  props.setOnMessageReceived(onMessageReceived);
  //웹소켓 코드 끝

  //첫 렌더링될 때만 입력받는 input태그로 focus
  useEffect(() => {
    myChar = document.getElementById("myCharacter");
    myChar.style.background = myColor;
    myChar.style.display = "block";
    console.log("myColor: ", myColor);
    console.log("myChar: ", myChar);
    mapFocus.current.focus();

    //랜더링 종료 시 인터벌 종료
    return () => {
      console.log("render return");

      //랜더링 종료될 때 leave 신호 전송
      clearInterval(inputCheck);
      clearInterval(sendMyPos);
      dispatch(setX(pX.current));
      dispatch(setY(pY.current));
      props.subGatherRoom.unsubscribed();
    };
  }, []);

  //약 60fps로 위치 정보 전송, 움직일 때만 전송
  const sendMyPos = setInterval(() => {
    if (client2) {
      if (keyPress[87] || keyPress[83] || keyPress[65] || keyPress[68]) {
        client2.publish({
          destination: sockADR,
          headers: { "content-type": "application/json" },
          body: JSON.stringify({
            header: {
              type: "interact",
            },
            data: {
              nickname: myNickName,
              status: "move",
              chairNum: null,
              x: pX.current,
              y: pY.current,
            },
          }),
          skipContentLengthHeader: true,
        });
      }
    }
  }, 16);

  //부드러운 움직임을 위한 변수 선언
  //일정 초마다 키 입력 확인
  const inputCheck = setInterval(() => {
    if (keyPress[87]) pY.current -= speed;
    if (keyPress[83]) pY.current += speed; // down - s
    if (keyPress[65]) pX.current -= speed; // left - a
    if (keyPress[68]) pX.current += speed;

    //테이블 충돌 감지
    if (
      pX.current < table.tbLeft + table.width &&
      pX.current + charSize > table.tbLeft &&
      pY.current < table.tbTop + table.height &&
      charSize + pY.current > table.tbTop
    ) {
      if (keyPress[87]) pY.current += speed;
      if (keyPress[83]) pY.current -= speed; // down - s
      if (keyPress[65]) pX.current += speed; // left - a
      if (keyPress[68]) pX.current -= speed;
    }

    seatNum = -1;
    //의자 충돌 감지
    for (let i = 0; i < 8; i++) {
      if (
        chairs[i][1] < pX.current + charSize &&
        chairs[i][1] + chairSize > pX.current &&
        chairs[i][0] < charSize + pY.current &&
        chairs[i][0] + chairSize > pY.current
      ) {
        seatNum = i;
        //자리에 앉았을 시 소켓으로 준비 완료 전송
        if (seatNum !== seatChange) {
          seatNothing = true;
          console.log("SeatNum: ", seatNum);
          console.log("send ready");
          seatChange = i;

          client2.publish({
            destination: "/sub/room/0",
            headers: { "content-type": "application/json" },
            body: JSON.stringify({
              header: {
                type: "interact",
              },
              data: {
                nickname: myNickName,
                status: "ready",
                chairNum: seatChange,
                y: pY.current,
                x: pX.current,
              },
            }),
            skipContentLengthHeader: true,
          });
        }
        //자리와 이전 자리가 같을 시
      }
    }
    //자리에서 벗어났을 시 소켓으로 준비 완료 해제 전송
    if (seatNum === -1 && seatNothing === true) {
      console.log("send not ready");
      seatCnt = 0;
      seatNothing = false;

      client2.publish({
        destination: "/sub/room/0",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "interact",
          },
          data: {
            nickname: myNickName,
            status: "stand",
            chairNum: -1,
            y: pY.current,
            x: pX.current,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
    //외부 벽 충돌 감지
    if (pY.current < 0) pY.current = 0;
    if (pY.current >= mapSize - charSize) pY.current = mapSize - charSize;
    if (pX.current < 0) pX.current = 0;
    if (pX.current >= mapSize - charSize) pX.current = mapSize - charSize;

    //캐릭터 좌표 최신화
    myChar.style.left = pX.current + "px";
    myChar.style.top = pY.current + "px";
  }, 16);

  //맵 클릭 시 입력 포커싱 지정
  const changeFocus = () => {
    mapFocus.current.focus();
  };

  //키 눌렀을 때 해당 값 true로 변환
  const handleKeyDown = (e) => {
    keyPress[e.keyCode.toString()] = true;
  };
  //키 땠을 때 해당 값 false로 변환
  const handleKeyUp = (e) => {
    keyPress[e.keyCode.toString()] = false;
    client2.publish({
      destination: sockADR,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "interact",
        },
        data: {
          nickname: myNickName,
          status: "move",
          chairNum: null,
          x: pX.current,
          y: pY.current,
        },
      }),
      skipContentLengthHeader: true,
    });

    console.log(pX.current + " " + pY.current);
  };

  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        height: "100%",
      }}
    >
      <button
        onClick={() => {
          console.log(pX.current + " " + pY.current);
        }}
      >
        확인
      </button>
      <input
        id='inputTag'
        value='준비 중'
        type='text'
        ref={mapFocus}
        onKeyDown={handleKeyDown}
        onKeyUp={handleKeyUp}
        readOnly
        style={{
          border: "none",
          cursor: "default",
          margin: "0",
          position: "absolute",
          top: 5,
          left: 5,
        }}
      />
      <div
        id='map'
        onClick={changeFocus}
        style={{
          width: mapSize,
          height: mapSize,
          background: "orange",
          position: "relative",
          left: 0,
          top: 0,
        }}
      >
        <div
          id='table'
          style={{
            width: table.width,
            height: table.height,
            left: table.tbLeft,
            top: table.tbTop,
            background: "green",
            position: "absolute",
          }}
        ></div>

        {chairs.map((e, idx) => {
          return (
            <div
              key={idx}
              id={"chair" + idx}
              style={{
                top: e[0],
                left: e[1],
                height: chairSize,
                width: chairSize,
                background: "blue",
                position: "absolute",
              }}
            ></div>
          );
        })}
        <div
          id='myCharacter'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            position: "absolute",
            color: myColor,
            display: "none",
          }}
        >
          <div style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}>
            {myNickName !== undefined ? myNickName : "need login"}
          </div>
        </div>
        {/* 다른 유저의 캐릭터 */}

        <div
          id='char1'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char1Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char2'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char2Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char3'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char3Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char4'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
