import React, { useState, useEffect, useImperativeHandle, forwardRef } from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { Helper } from "../RoomNavCom/Helper";
//MUI 모달 참조: https://mui.com/material-ui/react-modal/
import Modal from "@mui/material/Modal";
import { GameSetting } from "../RoomNavCom/GameSetting";
import { selectUser } from "../../redux/slice/UserSlice";
import { useSelector, useDispatch } from "react-redux";
//라우터 이동 위한 클래스
import { useNavigate } from "react-router-dom";

import { selectColor, selectX, selectY, setColor, setX, setY } from "../../redux/slice/CharSlice";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 500,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

export const NaviBar2 = forwardRef((props, ref) => {
  console.log("NaviBar2 리랜더링 체크");
  const dispatch = useDispatch();
  const roomNum = useSelector((state) => state.roomNum);
  const subAddr = `/sub/room/${roomNum}`;
  const pubAddr = `/pub/room/${roomNum}`;
  const myNickName = useSelector(selectUser).nickname;
  //useNavigate 객체 생성
  const navigate = useNavigate();

  const handleGameStart = () => {
    props.client.publish({
      destination: `${pubAddr}/game`,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "start",
        },
        data: null,
      }),
      skipContentLengthHeader: true,
    });
  };
  const returnListPage = () => {
    //방을 나갈 때 redux의 내 위치 초기화
    dispatch(setY(600));
    dispatch(setX(350));
    //채팅 이벤트 삭제
    document.getElementById("text").removeEventListener("keydown", ({ key, composed, target }) => {
      console.log(key + " " + composed + " " + target);
      if (key === "Enter" && composed) {
        props.sendData({ nickname: myNickName, message: target.value });
        target.value = "";
      }
    });
    //leave 신호 전송
    props.client.publish({
      destination: pubAddr,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "leave",
        },
        data: {
          nickname: myNickName,
        },
      }),
      skipContentLengthHeader: true,
    });
    navigate("/roomList");
  };

  const [SwitchModalValue, setSwitchModalValue] = useState(0);
  const theme = createTheme({
    typography: {
      fontSize: 10,
    },
  });

  const [open, setOpen] = useState(false);
  const handleOpen = (value) => {
    setSwitchModalValue(value);
    setOpen(true);
  };

  const handleClose = () => setOpen(false);

  const SwitchModals = () => {
    if (SwitchModalValue === 0) {
      return <Helper />;
    } else if (SwitchModalValue === 1) {
      return <GameSetting />;
    }
  };
  return (
    <div>
      <ThemeProvider theme={theme}>
        <Stack spacing={1} direction='row'>
          <Button
            variant='contained'
            size='small'
            onClick={() => {
              handleGameStart();
            }}
          >
            게임시작
          </Button>
          <Button
            variant='contained'
            size='small'
            onClick={() => {
              handleOpen(0);
            }}
          >
            도움말
          </Button>
          <Button
            variant='contained'
            size='small'
            onClick={() => {
              handleOpen(1);
            }}
          >
            게임설정
          </Button>
          <Button variant='contained' size='small'>
            환경설정
          </Button>
          <Button
            variant='contained'
            size='small'
            onClick={() => {
              returnListPage();
            }}
          >
            나가기
          </Button>
        </Stack>
      </ThemeProvider>

      <Modal open={open} onClose={handleClose}>
        <Box sx={style}>
          <SwitchModals />
        </Box>
      </Modal>
    </div>
  );
});

export default NaviBar2;
