import React, { useEffect, useMemo, useState, useRef } from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

import { selectUser } from "../../redux/slice/UserSlice";

//socktjs
import SockJS from "sockjs-client";

//redux
import { useSelector, useDispatch } from "react-redux";
import {} from "../../redux/slice/UserSlice";

const StompJs = require("@stomp/stompjs");

//===================================================
//about mui tabs
function TabPanel(props) {
  console.log(props.skD);
  const { children, value, index, ...other } = props;

  return (
    <div
      role='tabpanel'
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}
//end mui tabs
//===================================================

export default function SideBar() {
  const subChat = useRef();
  const [value, setValue] = useState(0);
  const roomNum = useSelector((state) => state.roomNum);
  const subAddr = `/sub/room/${roomNum}`;
  const pubAddr = `/sub/room/${roomNum}`;
  const acToken = useSelector(selectUser).accessToken;
  const client = useMemo(
    () =>
      new StompJs.Client({
        connectHeaders: {},
        // debug: function (str) {
        //   console.log(str);
        // },
        reconnectDelay: 5000,
        heartbeatIncoming: 4000,
        heartbeatOutgoing: 4000,
      }),
    []
  );
  client.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };
  const onMessageReceived = (payload) => {
    let data = JSON.parse(payload);
    console.log("side data: ", data);
    // if(data.header.type === "chat"){
    //   console.log(data.);
    // }
  };

  client.onConnect = (frame) => {
    console.log("start chat con");
    subChat.current = client.subscribe(subAddr, onMessageReceived);
  };

  client.onWebSocketClose = () => {
    console.log("close chat con");
  };

  //에러 발생시 실행되는 코드
  client.onStompError = function (frame) {
    console.log("Broker reported error: " + frame.headers["message"]);
    console.log("Additional details: " + frame.body);
  };

  useEffect(() => {
    // client.activate();

    return () => {
      client.publish({
        destination: pubAddr,
        headers: { "content-type": "application/json", token: acToken },
        body: JSON.stringify({
          header: {
            type: "leave",
          },
          data: {
            nickname: "",
          },
        }),
        skipContentLengthHeader: true,
      });
      console.log("close renderer");
    };
  }, []);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{ width: "100%", height: 800 }}>
      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs value={value} onChange={handleChange} aria-label='basic tabs example'>
          <Tab label='채팅' {...a11yProps(0)} />
          <Tab label='대기실 유저' {...a11yProps(1)} />
          <Tab label='친구' {...a11yProps(2)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}></TabPanel>
      <TabPanel value={value} index={1}>
        Item Tw
      </TabPanel>
      <TabPanel value={value} index={2}>
        Item Th
      </TabPanel>
    </Box>
  );
}

function ChatBar() {
  return <span>this is ChatBar</span>;
}

function UserBar() {
  return <span>this is UserBar</span>;
}
