import React, { useEffect, useRef, useState } from "react";

//의사, 경찰, 마피아 수 검사
export const GameSetting = (props) => {
    const [Limit, setLimit] = useState([5, 6, 7, 8]);
    const [Doc, setDoc] = useState();
    const [Cop, setCop] = useState();
    const [Maf, setMaf] = useState();

    const inputFocus = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        inputFocus.current.focus();
    }, []);
    const InputLimit = () => {
        console.log(Limit);
        Limit.map((data, idx) => {
            return <option key={idx}>data</option>;
        });
    };
    return (
        <div>
            <label>인원 수 : </label>
            <select ref={inputFocus}>
                <InputLimit />
            </select>
            <label>의사 수 : </label>
            <label>경찰 수 : </label>
            <label>마피아 수 : </label>
            <button>확인</button>
            <button>취소</button>
        </div>
    );
};
