import React, { useRef, useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { SavePosX, selectPosX } from "../../redux/slice/MapPosXSlice";
import { SavePosY, selectPosY } from "../../redux/slice/MapPosYSlice";
import { selectUser } from "../../redux/slice/UserSlice";
import { useBeforeunload } from "react-beforeunload";

//ÌòÑÏû¨ Î∞© Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ Ìï®Ïàò
import { selectCntCivil } from "../../redux/slice/CntCivilSlice";
import { selectCntCop } from "../../redux/slice/CntCopSlice";
import { selectCntDoc } from "../../redux/slice/CntDocSlice";
import { selectCntLimit } from "../../redux/slice/CntLimitSlice";
import { selectCntMaf } from "../../redux/slice/CntMafSlice";
import { selectRoomTitle } from "../../redux/slice/RoomTitleSlice";

import { selectCntCon, increCon, decreCon, setCon } from "../../redux/slice/CntConSlice";
//Î∞© Î≤àÌò∏ ÌïÑÏöî

//socktjs
import SockJS from "sockjs-client";

import { Client, Message } from "@stomp/stompjs";

//memo : Í∞íÏùÑ Î©îÎ™®Ïù¥
//callback : Ìï®ÏàòÎ•º Î©îÎ™®Ïù¥
export default function PlayMap() {
  let startYX = { y: 650, x: 300 };
  let subGatherRoom = null;
  const myNickName = useSelector(selectUser).nickname;
  const myColor = "#" + Math.round(Math.random() * 0xffffff).toString(16);
  const roooNum = 0;
  const sockADR = `/sub/room/${roooNum}`;
  const dispatch = useDispatch();
  let seatNum, seatChange, seatCnt, seatNothing;
  const mapSize = 700;
  const charSize = 50;
  const chairSize = 20;
  const table = {
    tbTop: 100,
    tbLeft: 200,
    width: 300,
    height: 300,
  };
  const chairs = [
    [40, 300],
    [40, 400],
    [160, 100],
    [300, 100],
    [450, 300],
    [450, 400],
    [160, 550],
    [300, 550],
  ];

  let beforeReunloadValue = "";

  useBeforeunload((e) => {
    if (beforeReunloadValue !== "aa") {
      e.preventDefault();
      e.returnValue = "abcd";
      console.log(e);
    }
  });

  //ÏôÑÏ†ÑÌûà ÌéòÏù¥ÏßÄÎ•º Îñ†ÎÇ† Îïå
  window.addEventListener("unload", () => {
    console.log("ü§£ÌéòÏù¥ÏßÄÎ•º Îñ†ÎÇòÍ≤†ÏäµÎãàÎã§.");
  });
  //Îßµ ÌÅ¨Í∏∞
  //Ìè¨Ïª§Ïã± ÏßÄÏ†ïÏùÑ ÏúÑÌïú Î≥ÄÏàò
  const mapFocus = useRef();
  //divÌÉúÍ∑∏Î•º ÏïÑÎ∞îÌÉÄ Í∞ùÏ≤¥Î°ú ÏßÄÏ†ï
  let myChar;
  //ÌÇ§ ÏûÖÎ†• Ïãú Í∞í Ï†ÄÏû• Î∞∞Ïó¥
  let keyPress = {},
    pX = useRef(startYX.x),
    pY = useRef(startYX.y),
    speed = 5;
  let otherUserData = {},
    remainNum = { 1: false, 2: false, 3: false, 4: false, 5: false, 6: false, 7: false };
  //ÏõπÏÜåÏºì ÏÑ†Ïñ∏ Î∞è Ï¥àÍ∏∞Ìôî
  //SockJs + StompJs
  var StompJs = require("@stomp/stompjs");

  const client2 = new StompJs.Client({
    //websocket Ï£ºÏÜåÎßå ÏûÖÎ†• Í∞ÄÎä• * ws://, wss:// Î°ú ÏãúÏûë
    // brokerURL: "https://i7d106.p.ssafy.io:8080/ws",
    connectHeaders: {},
    // debug: function (str) {
    //   console.log(str);
    // },
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  client2.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };

  client2.onConnect = (frame) => {
    //Íµ¨ÎèÖÌïòÎäî Í≥≥
    subGatherRoom = client2.subscribe("/sub/room/0", onMessageReceived);
    //Ïú†Ï†Ä Î∞© ÏûÖÏû•
    userJoin();
    //1Ï¥àÎßàÎã§
  };

  //ÏÜåÏºìÌÜµÏã†Ïù¥ Ï¢ÖÎ£åÎêòÏóàÏùÑ Îïå Ïã§ÌñâÎêòÎäî ÏΩîÎìú
  client2.onWebSocketClose = () => {};

  //ÏóêÎü¨ Î∞úÏÉùÏãú Ïã§ÌñâÎêòÎäî ÏΩîÎìú
  client2.onStompError = function (frame) {
    // Will be invoked in case of error encountered at Broker
    // Bad login/passcode typically will cause an error
    // Complaint brokers will set `message` header with a brief message. Body may contain details.
    // Compliant brokers will terminate the connection after any error
    console.log("Broker reported error: " + frame.headers["message"]);
    console.log("Additional details: " + frame.body);
  };
  client2.activate();

  //Ïõπ ÏÜåÏºì ÏΩîÎìú ÎÅù

  const userJoin = () => {
    document.getElementById("myCharacter").style.background = myColor;
    document.getElementById("myCharacter").style.display = "block";
    client2.publish({
      destination: sockADR,
      headers: { "content-type": "application/json" },
      body: JSON.stringify({
        header: {
          type: "join",
        },
        data: {
          nickname: myNickName,
          color: myColor,
        },
      }),
      skipContentLengthHeader: true,
    });
  };

  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    //ÎÇ¥Í∞Ä ÏÜ°Ïã†Ìïú Ï†ïÎ≥¥Îäî Ï≤òÎ¶¨ X
    if (payloadData.data.nickname === myNickName) return;

    //ÌÉÄ Ïú†Ï†Ä Ï†ïÎ≥¥ Ï≤òÎ¶¨
    if (payloadData.header.type === "join") {
      for (let i in remainNum) {
        //Îπà ÏûêÎ¶¨Ïóê ÎÑ£Í∏∞
        if (!remainNum[i]) {
          remainNum[i] = true;
          //ÏÉâÎ≥ÄÍ≤Ω => Î≥¥Ïù¥Í∏∞
          otherUserData[payloadData.data.nickname] = { color: payloadData.data.color, num: i };
          document.getElementById(`char${i}`).style.left = startYX.x;
          document.getElementById(`char${i}`).style.top = startYX.y;

          document.getElementById(`char${i}`).style.background = payloadData.data.color;
          document.getElementById(`char${i}`).style.display = "block";
          document.getElementById(`char${i}Name`).innerText = payloadData.data.nickname;
          break;
        }
      }
    } else if (payloadData.header.type === "leave") {
      let userNum = otherUserData[payloadData.data.nickname].num;
      remainNum[userNum] = false;
      document.getElementById(`char${userNum}`).style.display = "none";
      document.getElementById(`char${userNum}Name`).innerText = "";
    }
    //Ï∫êÎ¶≠ÌÑ∞ ÏõÄÏßÅÏùº Îïå
    else if (payloadData.header.type === "interact") {
      //ÏúÑÏπò Î≥ÄÍ≤Ω
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.left =
        payloadData.data.x + "px";
      document.getElementById(`char${otherUserData[payloadData.data.nickname].num}`).style.top =
        payloadData.data.y + "px";
      otherUserData[payloadData.data.nickname]["y"] = payloadData.data.y;
      otherUserData[payloadData.data.nickname]["x"] = payloadData.data.x;

      //ÏùòÏûêÏóê ÏïâÏïòÏùÑ Îïå Ìï¥Îãπ ÏùòÏûê ÎπÑÌôúÏÑ±Ìôî
      if (payloadData.data.status === "ready") {
      }
      //ÏùòÏûê ÏùºÏñ¥ÏÑ∞ÏùÑ Îïå Ìï¥Îãπ ÏùòÏûê ÌôúÏÑ±Ìôî
      else if (payloadData.data.status === "stand") {
      }
    } else if (payloadData.header.type === "start") {
      console.log(payloadData.data);
    } else if (payloadData.header.type === "setting") {
      console.log(payloadData.data);
    }
  };

  //ÏõπÏÜåÏºì ÏΩîÎìú ÎÅù

  //Î†åÎçîÎßÅÎê† ÎïåÎßàÎã§ myCharÍ∞Ä #myCharacter Î•º Í∞ÄÎ•¥ÌÇ¥
  useEffect(() => {
    myChar = document.getElementById("myCharacter");
    console.log("myChar rendered");
    //ÎûúÎçîÎßÅ Ï¢ÖÎ£å Ïãú Ïù∏ÌÑ∞Î≤å Ï¢ÖÎ£å
    return () => {
      clearInterval(inputCheck);

      //ÎûúÎçîÎßÅ Ï¢ÖÎ£åÎê† Îïå leave Ïã†Ìò∏ Ï†ÑÏÜ°
      console.log("render returned");
      client2.publish({
        destination: "/sub/room/0",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "leave",
          },
          data: {
            nickname: myNickName,
          },
        }),

        skipContentLengthHeader: true,
      });
      //ÎûúÎçîÎßÅÏù¥ Ï¢ÖÎ£åÎê† Îïå ÏÜåÏºì ÌÜµÏã† Ï¢ÖÎ£å
      client2.deactivate();
    };
  });

  //ÏïΩ 60fpsÎ°ú ÏúÑÏπò Ï†ïÎ≥¥ Ï†ÑÏÜ°, ÏõÄÏßÅÏùº ÎïåÎßå Ï†ÑÏÜ°
  const sendMyPos = setInterval(() => {
    if (client2) {
      if (keyPress[87] || keyPress[83] || keyPress[65] || keyPress[68]) {
        client2.publish({
          destination: sockADR,
          headers: { "content-type": "application/json" },
          body: JSON.stringify({
            header: {
              type: "interact",
            },
            data: {
              nickname: myNickName,
              status: "move",
              chairNum: null,
              x: pX.current,
              y: pY.current,
            },
          }),
          skipContentLengthHeader: true,
        });
      }
    }
  }, 16);

  //Î∂ÄÎìúÎü¨Ïö¥ ÏõÄÏßÅÏûÑÏùÑ ÏúÑÌïú Î≥ÄÏàò ÏÑ†Ïñ∏
  //ÏùºÏ†ï Ï¥àÎßàÎã§ ÌÇ§ ÏûÖÎ†• ÌôïÏù∏
  const inputCheck = setInterval(() => {
    if (keyPress[87]) pY.current -= speed;
    if (keyPress[83]) pY.current += speed; // down - s
    if (keyPress[65]) pX.current -= speed; // left - a
    if (keyPress[68]) pX.current += speed;

    //ÌÖåÏù¥Î∏î Ï∂©Îèå Í∞êÏßÄ
    if (
      pX.current < table.tbLeft + table.width &&
      pX.current + charSize > table.tbLeft &&
      pY.current < table.tbTop + table.height &&
      charSize + pY.current > table.tbTop
    ) {
      if (keyPress[87]) pY.current += speed;
      if (keyPress[83]) pY.current -= speed; // down - s
      if (keyPress[65]) pX.current += speed; // left - a
      if (keyPress[68]) pX.current -= speed;
    }

    seatNum = -1;
    //ÏùòÏûê Ï∂©Îèå Í∞êÏßÄ
    for (let i = 0; i < 8; i++) {
      if (
        chairs[i][1] < pX.current + charSize &&
        chairs[i][1] + chairSize > pX.current &&
        chairs[i][0] < charSize + pY.current &&
        chairs[i][0] + chairSize > pY.current
      ) {
        seatNum = i;
        //ÏûêÎ¶¨Ïóê ÏïâÏïòÏùÑ Ïãú ÏÜåÏºìÏúºÎ°ú Ï§ÄÎπÑ ÏôÑÎ£å Ï†ÑÏÜ°
        if (seatNum !== seatChange) {
          seatNothing = true;
          document.getElementById("inputTag").value = "Ï§ÄÎπÑ ÏôÑÎ£å";
          console.log("SeatNum: ", seatNum);
          console.log("send ready");
          seatChange = i;

          client2.publish({
            destination: "/sub/room/0",
            headers: { "content-type": "application/json" },
            body: JSON.stringify({
              header: {
                type: "interact",
              },
              data: {
                nickname: myNickName,
                status: "ready",
                chairNum: seatChange,
                y: pY.current,
                x: pX.current,
              },
            }),
            skipContentLengthHeader: true,
          });
        }
        //ÏûêÎ¶¨ÏôÄ Ïù¥Ï†Ñ ÏûêÎ¶¨Í∞Ä Í∞ôÏùÑ Ïãú
      }
    }
    //ÏûêÎ¶¨ÏóêÏÑú Î≤óÏñ¥ÎÇ¨ÏùÑ Ïãú ÏÜåÏºìÏúºÎ°ú Ï§ÄÎπÑ ÏôÑÎ£å Ìï¥Ï†ú Ï†ÑÏÜ°
    if (seatNum === -1 && seatNothing === true) {
      console.log("send not ready");
      document.getElementById("inputTag").value = "Ï§ÄÎπÑ Ï§ë";
      seatCnt = 0;
      seatNothing = false;

      client2.publish({
        destination: "/sub/room/0",
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "interact",
          },
          data: {
            nickname: myNickName,
            status: "stand",
            chairNum: -1,
            y: pY.current,
            x: pX.current,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
    if (pY.current < 0)
      //Ïô∏Î∂Ä Î≤Ω Ï∂©Îèå Í∞êÏßÄ
      pY.current = 0;
    if (pY.current >= mapSize - charSize) pY.current = mapSize - charSize;
    if (pX.current < 0) pX.current = 0;
    if (pX.current >= mapSize - charSize) pX.current = mapSize - charSize;

    //Ï∫êÎ¶≠ÌÑ∞ Ï¢åÌëú ÏµúÏã†Ìôî
    myChar.style.left = pX.current + "px";
    myChar.style.top = pY.current + "px";
  }, 16);

  //Ï≤´ Î†åÎçîÎßÅÎê† ÎïåÎßå ÏûÖÎ†•Î∞õÎäî inputÌÉúÍ∑∏Î°ú focus
  useEffect(() => {
    console.log("useEffect focus once");
    mapFocus.current.focus();
  }, []);

  //Îßµ ÌÅ¥Î¶≠ Ïãú ÏûÖÎ†• Ìè¨Ïª§Ïã± ÏßÄÏ†ï
  const changeFocus = () => {
    mapFocus.current.focus();
  };

  //ÌÇ§ ÎàåÎ†ÄÏùÑ Îïå Ìï¥Îãπ Í∞í trueÎ°ú Î≥ÄÌôò
  const handleKeyDown = (e) => {
    keyPress[e.keyCode.toString()] = true;
  };
  //ÌÇ§ Îï†ÏùÑ Îïå Ìï¥Îãπ Í∞í falseÎ°ú Î≥ÄÌôò
  const handleKeyUp = (e) => {
    if (keyPress[87] || keyPress[83] || keyPress[65] || keyPress[68]) {
      client2.publish({
        destination: sockADR,
        headers: { "content-type": "application/json" },
        body: JSON.stringify({
          header: {
            type: "interact2",
          },
          data: {
            nickname: myNickName,
            status: "move",
            chairNum: null,
            x: pX.current,
            y: pY.current,
          },
        }),
        skipContentLengthHeader: true,
      });
    }
    keyPress[e.keyCode.toString()] = false;
  };

  //Ï§ëÍ∞ÑÏóê Í≤åÏûÑÎ∞©ÏùÑ ÎÇòÍ∞à Îïå ÏßÑÏßú ÎÇòÍ∞à Í≤ÉÏù∏ÏßÄ ÌôïÏù∏ Î∞õÍ∏∞
  const [Value, setValue] = useState("");
  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        height: "100%",
      }}
    >
      <input
        id='inputTag'
        onChange={(e) => setValue(e.target.value)}
        value='Ï§ÄÎπÑ Ï§ë'
        type='text'
        ref={mapFocus}
        onKeyDown={handleKeyDown}
        onKeyUp={handleKeyUp}
        readOnly
        style={{
          border: "none",
          cursor: "default",
          margin: "0",
          position: "absolute",
          top: 5,
          left: 5,
        }}
      />
      <div
        id='map'
        onClick={changeFocus}
        style={{
          width: mapSize,
          height: mapSize,
          background: "orange",
          position: "relative",
          left: 0,
          top: 0,
        }}
      >
        <div
          id='table'
          style={{
            width: table.width,
            height: table.height,
            left: table.tbLeft,
            top: table.tbTop,
            background: "green",
            position: "absolute",
          }}
        ></div>

        {chairs.map((e, idx) => {
          return (
            <div
              key={idx}
              id={"chair" + idx}
              style={{
                top: e[0],
                left: e[1],
                height: chairSize,
                width: chairSize,
                background: "blue",
                position: "absolute",
              }}
            ></div>
          );
        })}
        <div
          id='myCharacter'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            position: "absolute",
            display: "none",
          }}
        >
          <div style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}>
            {myNickName}
          </div>
        </div>
        {/* Îã§Î•∏ Ïú†Ï†ÄÏùò Ï∫êÎ¶≠ÌÑ∞ ÏûëÏÑ± */}

        <div
          id='char1'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char1Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char2'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char2Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char3'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char3Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char4'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char4Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char5'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char5Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char6'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char6Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
        <div
          id='char7'
          style={{
            margin: `0px`,
            width: charSize,
            height: charSize,
            background: "red",
            position: "absolute",
            display: "none",
            left: startYX.x,
            top: startYX.y,
          }}
        >
          <div
            id='char7Name'
            style={{ top: -20, width: 200, position: "absolute", left: -100 + 25 }}
          ></div>
        </div>
      </div>
    </div>
  );
}
