import React, { useEffect, useRef, useState } from "react";

//의사, 경찰, 마피아 수 검사
export const GameSetting = (props) => {
    const [Limit, setLimit] = useState([
        { value: 5 },
        { value: 6 },
        { value: 7 },
        { value: 8 },
    ]);
    const [Doc, setDoc] = useState(0);
    const [Cop, setCop] = useState(0);
    const [Maf, setMaf] = useState(0);

    const inputFocus = useRef();
    //소켓 통신으로 변경 완료 알림
    //1번 조건에 맞는지 확인
    useEffect(() => {
        inputFocus.current.focus();
    }, []);
    // function InputLimit() {
    //     return Limit.map((data, idx) => {
    //         return <option key={idx}>{data}</option>;
    //     });
    // }
    //최대 인원 넣기
    const InputLimit = () => {
        console.log("InputLimit");
        return Limit.map((data, idx) => {
            return <option key={idx}>{data}</option>;
        });
    };

    const selectBox = (props) => {
        return (
            <select>
                {props.options.map((option) => {
                    <option key={option.value} value={option.value}>
                        {option.value}
                    </option>;
                })}
            </select>
        );
    };
    return (
        <div>
            <label>인원 수 : </label>
            <selectBox options={Limit} />
            <label>의사 수 : </label>

            <label>경찰 수 : </label>
            <label>마피아 수 : </label>
            <button>확인</button>
            <button>취소</button>
        </div>
    );
};
