import { createSlice } from "@reduxjs/toolkit";

export const CntConSlice = createSlice({
    name: "CntConSlice",
    initialState: {
        value: 0,
    },
    reducers: {
        incrementCrntCon: (state) => {
            state.value += 1;
        },

        decrementCrntCon: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = CntConSlice.actions;

export const selectConCnt = (state) => state.conCnt.value;

export default CntConSlice.reducer;
