import React, { useState,useEffect } from "react";
import { Container, styleTableContainer, styleButton } from '../style.js';
import { Table, TableHead, TableFooter, TableContainer, TableBody, Paper, TableRow, TableCell, TablePagination, Button } from '@mui/material';
import { useNavigate, useParams } from  "react-router-dom";
import {useSelector} from "react-redux"
import axios from 'axios';


const UserDetailPage = (props) => {

  const token = useSelector(state=>state.user.accessToken)
  const { userId } = useParams();
  const [user, setUser] = useState({});
  const [reportedList, setReportedList] = useState([]);
  const [reportingList, setReportingList] = useState([]);

  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  //유저 정보 불러오기
  const getUser = () =>{
    axios.get(`/api/admin/${userId}`,{
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    }).then(res =>{
      setUser(res.data);
    });
  }
  useEffect(() => {
    getUser();
  }, []);

  //유저를 대상으로한 신고목록 가져오기
  useEffect(() => {
    axios.get(`/api/admin/${userId}/reported-list`,{
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    }).then(res =>{
      setReportedList(res.data);
    });
  }, []);

  //유저가 신고한 목록 가져오기
  useEffect(() => {
    axios.get(`/api/admin/${userId}/reporting-list`,{
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    }).then(res =>{
      setReportingList(res.data)
    });
  }, []);

  //페이지 이동용
  const navigate = useNavigate();
  const goUserListPage = () => {
    navigate("/users");
  };

  // 레드유저 등록,해제
  const changeRed = () => { 
    axios.post(`/api/admin/red/${userId}`,{},{
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      }
    }).then(r=>{
      getUser();
    })
  }


  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  }

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  }



  const check = () => {
    console.log(user);
    console.log(reportedList);
    console.log(reportingList);
  }

  return (
    <Container>

      <button onClick={check}>check</button>
      
      <h2>유저 상세 조회 페이지</h2>
      <span>
        <Button style={styleButton} onClick={goUserListPage}>목록으로</Button>
      </span>
      <span>유저 닉네임: {user.nickname}</span>
      <span>이메일: {user.email}</span>
      <span> 가입일자: {user.createdAt}</span>
      <span> 신고당한 횟수: {user.reportedCount}</span>
      <span>{user.redUser ? <span>레드유저</span> : <span>일반유저</span>}
      {user.redUser ? <button onClick={changeRed}>레드유저 해제</button> : <button onClick={changeRed}>레드유저 등록</button>}
      </span>
      <span>전적: {user.winCount+user.loseCount}전 {user.winCount}승 {user.loseCount}패</span>
      
      <h2>신고한 내역</h2>
      <TableContainer style={styleTableContainer} component={Paper}>
        <Table size="medium">
          <TableHead>
            <TableRow>
              <TableCell>신고내용</TableCell>
              <TableCell>유저 이메일</TableCell>
              <TableCell>신고일시</TableCell>
            </TableRow>
          </TableHead>
            <TableBody>
              {reportingList
                .slice(page * rowsPerPage, (page + 1) * rowsPerPage)
                .map((reportData, i) => (
                  <TableRow key={i}>
                    <TableCell>{reportData.reportType}</TableCell>
                    <TableCell>{reportData.reportedUser}</TableCell>
                    <TableCell>{reportData.reportingAt}</TableCell>
                  </TableRow>
                ))}
          </TableBody>
          <TableFooter>
            <TableRow>
              <TablePagination
                page={page}
                count={reportingList.length}
                rowsPerPage={rowsPerPage}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>

      <h2>신고당한 내역</h2>
      <TableContainer style={styleTableContainer} component={Paper}>
        <Table size="medium">
          <TableHead>
            <TableRow>
              <TableCell>신고내용</TableCell>
              <TableCell>유저 이메일</TableCell>
              <TableCell>신고일시</TableCell>
            </TableRow>
          </TableHead>
            <TableBody>
              {reportedList
                .slice(page * rowsPerPage, (page + 1) * rowsPerPage)
                .map((reportedData, i) => (
                  <TableRow key={i}>
                    
                    <TableCell>{reportedData.reportType}</TableCell>
                    <TableCell>{reportedData.reportingUser}</TableCell>
                    <TableCell>{reportedData.reportingAt}</TableCell>
                  </TableRow>
                ))}
          </TableBody>
          <TableFooter>
            <TableRow>
              <TablePagination
                page={page}
                count={reportedList.length}
                rowsPerPage={rowsPerPage}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
              />
            </TableRow>
          </TableFooter>
        </Table>
      </TableContainer>



    </Container>
  );
};

export default UserDetailPage;
