import React, {
  useEffect,
  useCallback,
  useMemo,
  useState,
  useRef,
  forwardRef,
  useImperativeHandle,
} from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

import { selectUser } from "../../redux/slice/UserSlice";

//socktjs
import SockJS from "sockjs-client";

//redux
import { useSelector, useDispatch } from "react-redux";
import {} from "../../redux/slice/UserSlice";

const StompJs = require("@stomp/stompjs");

//===================================================
//about mui tabs
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role='tabpanel'
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}
//end mui tabs
//===================================================

export const SideBar = forwardRef((props, ref) => {
  console.log("sideBar 리랜더링 체크");
  useImperativeHandle(ref, () => ({
    // 부모 컴포넌트에서 사용할 함수를 선언
    onMessageReceived,
  }));
  const roomNum = useSelector((state) => state.roomNum);
  const pubAddr = `/sub/room/${roomNum}`;
  const myNickName = useSelector(selectUser).nickname;
  const [value, setValue] = useState(0);
  const [chats, setChats] = useState([]);
  const acToken = useSelector(selectUser).accessToken;
  props.client.webSocketFactory = () => {
    return new SockJS("https://i7d106.p.ssafy.io:8080/ws");
  };

  const onMessageReceived = (payload) => {
    let parsedData = JSON.parse(payload.body);
    if (parsedData.header.type === "chat") {
      console.log("sideBar chat receive: ", parsedData);
      chats.push({ nickname: parsedData.data.nickname, message: parsedData.data.message });
      setChats([...chats]);
    }
  };
  useEffect(() => {
    console.log("chats: ", chats);
  }, [chats]);

  useEffect(() => {
    console.log("one time rendering check");
    let textInput = document
      .getElementById("text")
      .addEventListener("keydown", ({ key, composed, target }) => {
        if (key === "Enter" && composed) {
          console.log(key + " " + composed + " " + target);
          props.client.publish({
            destination: pubAddr,
            headers: { "content-type": "application/json", token: acToken },
            body: JSON.stringify({
              header: {
                type: "chat",
              },
              data: {
                nickname: myNickName,
                message: target.value,
              },
            }),
            skipContentLengthHeader: true,
          });
          target.value = "";
        }
      });
    return () => {
      document.getElementById("text").removeEventListener("keydown");
      console.log("close renderer");
    };
  }, []);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{ width: "100%", height: 800 }}>
      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs value={value} onChange={handleChange} aria-label='basic tabs example'>
          <Tab label='채팅' {...a11yProps(0)} />
          <Tab label='대기실 유저' {...a11yProps(1)} />
          {/* <Tab label='친구' {...a11yProps(2)} /> */}
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
        {/* 채팅 내역 */}

        {chats.map((data, idx) => {
          return (
            <span key={data.message + idx}>
              <span>{data.nickname}</span>
              <span>{"\n"}</span>
              <span>{data.message}</span>
            </span>
          );
        })}
        <input id='text' type='text' />
      </TabPanel>
      <TabPanel value={value} index={1}>
        Item Tw
      </TabPanel>
      {/* <TabPanel value={value} index={2}>
        Item Th
      </TabPanel> */}
    </Box>
  );
});

export default SideBar;
