import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Container, styleButton, styleModal, styleTextField } from '../style.js';
import { Modal, Box, Button, TextField, Card } from '@mui/material';
import { useNavigate } from  "react-router-dom";
import { getUser } from "../redux/slice/UserSlice";
import styled from 'styled-components';
import axios from 'axios';


const SignUpPage = (props) => {
  const dispatch = useDispatch();

  const [modalOpen, setModalOpen] = useState(false);
  const [isValidEmail, setIsValidEmail] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const userSeq = useSelector(state => state.user.userSeq);
  // const [emailAuth, setEmailAuth] = useState(null);

  const navigate = useNavigate();

  const goEmailAuthPage = () => {
    if ( !isValidEmail) {
      handleModalOpen('이메일 중복검사를 해주세요.');
    } else if (!isSamePassword()) {
      handleModalOpen('비밀번호가 다릅니다.');
    } else {
      // setEmailAuth(true);
      
      // userSeq ? sendEmail() : signup()
      userSeq ? navigate('/emailauth') : signup()
    }
  };

  const signup = () => {
    axios({
      method: 'post',
      url: '/api/signup',
      headers: {
        'Content-Type': 'application/json',
      },
      data: {
        email,
        password,
      },
    })
    .then((response) => {
      // console.log('회원 정보 등록');
      dispatch(getUser({...response.data, email}));
      navigate('/emailauth');
    })
    .catch(error => {
      if (error.response.status === 401) {
        // setWrongInputData(true);
      }
    })
  }

  const handleModalOpen = (message) => {
    setModalMessage(message);
    setModalOpen(true);
  };
  const handleModalClose = () => {
    setModalOpen(false);
  };

  const isSamePassword = () => {
    return password === passwordCheck;
  };

  const checkEmail = () => {
    axios({
      method: 'post',
      url: '/api/checkEmail',
      headers: {
        'Content-Type': 'text/plain',
      },
      data: email,
    })
    .then(response => {
      if (response.data) {
        handleModalOpen('이미 사용 중인 이메일입니다.');
      }
      else {
        setIsValidEmail(true);
        handleModalOpen('사용 가능한 이메일입니다.');
      }
    })
    .catch(error => {
      if (error.response.status === 401) {
        // setWrongInputData(true);
      }
    })
  }

  return (
    <>
      <Modal
        open={modalOpen}
        onClose={handleModalClose}
        aria-labelledby="modal-title"
      >
        <Box sx={{ ...styleModal, width: 400 }}>
          <h2 id="modal-title">{modalMessage}</h2>
          <Button style={styleButton} onClick={handleModalClose}>확인</Button>
        </Box>
      </Modal>
        
        <Container>
          <Card 
            sx={{ height: "50%", width:"25%", }}>
            <h2>회원가입</h2>
           
            <Content>
              <TextField style={styleTextField} name="email" placeholder="E-mail" onChange={(e) => {setIsValidEmail(false); setEmail(e.target.value);}}/>
              <Button style={styleButton} onClick={() => {if (email.length) checkEmail(); else handleModalOpen('이메일을 입력해주세요.');}}>중복검사</Button>
            </Content>
            <br></br>
            <Content>
              <TextField style={styleTextField} type="password" placeholder="Password" name="password" onChange={(e) => {setPassword(e.target.value)}}/>
            </Content>
            <br></br>
            <Content>
              <TextField style={styleTextField} type="password" placeholder="check_Password" name="password-check" onChange={(e) => {setPasswordCheck(e.target.value)}}/>
            </Content>

            {!isSamePassword() ? <span>비밀번호가 다릅니다.</span> : null}
            <br></br>
            <Button style={styleButton} onClick={goEmailAuthPage}>이메일로 회원가입</Button>
       
          </Card>
        </Container>
    </>
  );
};

const Content = styled.div`
  overflow: hidden;
  display: block;
  text-align: center;
`

export default SignUpPage;
