import { createSlice } from "@reduxjs/toolkit";

export const GatherNumCopSlice = createSlice({
    name: "GatherNumCop",
    initialState: {
        value: 1,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementCrntCon, decrementCrntCon } = GatherNumCopSlice.actions;

export const selectConCnt = (state) => state.cop.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default GatherNumCopSlice.reducer;
