//react
import React, { useState } from "react";

//redux
import { useSelector } from "react-redux";
import { setNightTime, setTalkTime, setVoteTime } from "../../redux/slice/GameTimeSlice";

//mui library
import Grid from "@mui/material/Grid";
import { useDispatch } from "react-redux";
import { setCivil } from "../../redux/slice/CntCivilSlice";
import { setCop } from "../../redux/slice/CntCopSlice";
import { setDoc } from "../../redux/slice/CntDocSlice";
import { setLimit } from "../../redux/slice/CntLimitSlice";
import { setMaf } from "../../redux/slice/CntMafSlice";
import Stack from "@mui/material/Stack";

// import {
//     SetRoomTitle,
//     selectRoomTitle,
// } from "../../redux/slice/RoomTitleSlice";
//의사, 경찰, 마피아수 변경 기능

export const GameSetting = (props) => {
  const dispatch = useDispatch();
  const roomNum = useSelector((state) => state.roomNum);
  const token = useSelector((state) => state.user.accessToken);
  const [ArrLimit] = useState([5, 6, 7, 8]);
  const [StateLimit] = useState(5);
  const [StateDoc] = useState(0);
  let actLimit = [0, 1, 2, 3, 4, 5, 6, 7];
  const [StateCop] = useState(0);
  const [StateMaf, setStateMaf] = useState(0);
  const [stateTalkTime, setStateTalkTime] = useState(0);
  const [stateNightTime, setStateNightTime] = useState(0);
  const [statevoteTime, setStateVoteTime] = useState(0);
  const UpdateGameSetting = () => {
    const pubAddr = `/pub/room/${roomNum}`;

    let conLimit = document.getElementById("conLimit");
    let doc = document.getElementById("doc");
    let cop = document.getElementById("cop");
    let maf = document.getElementById("maf");
    let conLimitV = conLimit.options[conLimit.selectedIndex].value,
      docV = doc.options[doc.selectedIndex].value,
      copV = cop.options[cop.selectedIndex].value,
      mafV = maf.options[maf.selectedIndex].value;
    if (docV + copV + mafV > conLimitV) {
      console.log("역할 초과 합: ", docV + copV + mafV);
      document.getElementById("checkValue").innerText = "배정한 역할이 너무 많습니다.";
      return;
    } else if (mafV === 0) {
      document.getElementById("checkValue").innerText = "마피아는 최소 1명이여야 합니다.";
      return;
    }
    //선택하지 않았을 시

    if (
      isNaN(document.getElementById("voteTime").value) ||
      document.getElementById("nightTime").value ||
      document.getElementById("talkTime").value
    ) {
      document.getElementById("checkValue").innerText = "시간을 입력해주세요.";
      return;
    }

    dispatch(setLimit(conLimitV));
    dispatch(setDoc(docV));
    dispatch(setCop(copV));
    dispatch(setMaf(mafV));
    dispatch(setCivil(conLimitV - docV - copV - mafV));
    dispatch(setVoteTime(document.getElementById("voteTime").value));
    dispatch(setNightTime(document.getElementById("nightTime").value));
    dispatch(setTalkTime(document.getElementById("talkTime").value));

    console.log("before client pub");
    props.client.publish({
      destination: pubAddr,
      headers: { token: token, "content-type": "application/json" },
      body: JSON.stringify({
        header: { type: "setting" },
        data: {
          title: null,
          gameSeq: roomNum, // 게임 번호 (방 번호랑 다름)
          capacity: conLimitV, // 방 수용 인원
          mafia: mafV, // 마피아 수
          doctor: docV, // 의사 수
          police: copV, // 경찰 수
          talkTime: document.getElementById("talkTime").value, // 회의 시간 (초)
          voteTime: document.getElementById("voteTime").value, // 투표 시간 (초)
          nightTime: document.getElementById("nightTime").value, // 밤 시간 (초)
        },
      }),
      skipContentLengthHeader: true,
    });
    props.setOpen(false);
  };
  return (
    <React.Fragment>
      <Grid
        container
        spacing={2}
        sx={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: 400,
          color: "white",
          bgcolor: "rgba(220,220,220,0.3)",
          border: "2px solid #000",
          boxShadow: 24,
          p: 4,
          borderRadius: "20px",
          height: "50%",
        }}
      >
        <Stack direction='row' spacing={2}>
          {/* 인원 수 클릭 시 해당  */}

          <label>인원 수 : </label>
          <select id='conLimit' style={{ marginTop: "20px", height: "30px" }}>
            {ArrLimit.map((data, idx) => {
              return (
                <option key={idx} value={data}>
                  {data}
                </option>
              );
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>의사 수 : </label>
          <select id='doc' style={{ marginTop: "20px", height: "30px" }}>
            {actLimit.map((data, idx) => {
              return (
                <option key={idx} value={data}>
                  {data}
                </option>
              );
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>경찰 수 : </label>
          <select id='cop' style={{ marginTop: "20px", height: "30px" }}>
            {actLimit.map((data, idx) => {
              return (
                <option key={idx} value={data}>
                  {data}
                </option>
              );
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>마피아 수 : </label>
          <select id='maf' defaultValue={1} style={{ marginTop: "20px", height: "30px" }}>
            {actLimit.map((data, idx) => {
              return (
                <option key={idx} value={data}>
                  {data}
                </option>
              );
            })}
          </select>
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>낮 시간 : </label>
          <input id='talkTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>투표 시간 : </label>
          <input id='voteTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <Stack direction='row' spacing={2}>
          <label>밤 시간 : </label>
          <input id='nightTime' type='number' min='10' max='180' placeholder='10-180' />
        </Stack>
        <button
          onClick={() => {
            UpdateGameSetting();
          }}
        >
          확인
        </button>
        <div id='checkValue' style={{ color: "red" }}></div>
      </Grid>
    </React.Fragment>
  );
};
