import React, { useState } from "react";
import { Grid } from "@mui/material";
import OpenViduSession from "openvidu-react";
import axios from "axios";

import { useSelector, useDispatch } from "react-redux";
import { selectUser } from "../../redux/slice/UserSlice";

export default function AllCam() {
  const [Token, setToken] = useState();
  const [Session, setSession] = useState();
  const roomNum = 55;
  //axios로 토큰 받아오기
  const user = useSelector(selectUser);
  console.log("user: ", user);

  //토큰값 요구
  axios({
    url: `/api/room/${roomNum}/video-token`,
    method: "get",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${user.accessToken}`,
    },
  })
    .then((res) => {
      console.log(res);
      setToken(res.token);
      setSession(true);
    })
    .catch((err) => {
      console.log("err: ", err);
    });

  handlerJoinSessionEvent = () => {
    console.log("Join session");
  };

  handlerLeaveSessionEvent = () => {
    console.log("Leave session");
    this.setState({
      session: undefined,
    });
  };

  handlerErrorEvent = () => {
    console.log("Leave session");
  };

  return (
    <Grid container spacing={2}>
      <Grid item xs={4}>
        <Grid>
          <OpenViduSession
            id='opv-session'
            sessionName={"이청 테스트 룸0"}
            user={user.nickName}
            token={user.acc}
            joinSession={handlerJoinSessionEvent()}
            leaveSession={handlerLeaveSessionEvent()}
            error={handlerErrorEvent()}
          />
          <video autoPlay playsInline='true'></video>
        </Grid>
        <Grid>cam4</Grid>
        <Grid>cam6</Grid>
      </Grid>
      <Grid item xs={4}>
        <Grid>cam2</Grid>
        <Grid>empty</Grid>
        <Grid>cam7</Grid>
      </Grid>
      <Grid item xs={4}>
        <Grid>cam3</Grid>
        <Grid>cam5</Grid>
        <Grid>cam8</Grid>
      </Grid>
    </Grid>
  );
}
