import React, { useRef, useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { SavePosX, selectPosX } from "../../redux/slice/GatherMapPosXSlice";
import { SavePosY, selectPosY } from "../../redux/slice/GatherMapPosYSlice";
import { useBeforeunload } from "react-beforeunload";
import { chainPropTypes } from "@mui/utils";

export default function PlayMap(props) {
    const dispatch = useDispatch();
    const mapSize = 700;
    const charSize = 50;
    const tableData = {
        tbLeft: 200,
        tbTop: 100 - charSize,
        width: 300,
        height: 100,
    };
    //맵 크기
    //포커싱 지정을 위한 변수
    const mapFocus = useRef();
    //div태그를 아바타 객체로 지정
    let myChar;

    let keyPress = {},
        pX = useRef(useSelector(selectPosX)),
        pY = useRef(useSelector(selectPosY)),
        speed = 5;
    //렌더링될 때마다 myChar가 #myCharacter 를 가르킴
    useEffect(() => {
        myChar = document.getElementById("myCharacter");
        console.log("myChar rendered");
        //랜더링 종료 시 인터벌 종료
        return () => {
            clearInterval(inputCheck);

            console.log("pX : ", pX.current);
            console.log("pY : ", pY.current);
            dispatch(SavePosX(pX.current));
            dispatch(SavePosY(pY.current));
            console.log("render returned");
        };
    });

    //부드러운 움직임을 위한 변수 선언
    //일정 초마다 키 입력 확인
    const inputCheck = setInterval(() => {
        if (keyPress[87]) pY.current -= speed;
        if (keyPress[83]) pY.current += speed; // down - s
        if (keyPress[65]) pX.current -= speed; // left - a
        if (keyPress[68]) pX.current += speed;

        //테이블 충돌 감지
        //캐릭터 네 점이 테이블과 겹치는지 확인
        //캐릭터의 x축이 테이블 x축 안에 포함될 때
        //좌상(charY, charX), 우상(charY, charX+charSize)
        //좌하(charY+charSize, charX), 우하(charY+charSize, charX+charSize)

        //좌상 기준
        if (
            tableData.tbLeft < pX.current &&
            pX.current < tableData.tbLeft + tableData.width
        ) {
            console.log("ok1");
            if (pY.current < tableData.height + tableData.tbTop + charSize) {
                console.log("ok2");
                pY.current += 5;
            }
        }

        if (
            tableData.tbTop + 50 < pY.current &&
            pY.current < tableData.tbTop + 50 + tableData.height
        ) {
            console.log("ok3");
            if (pX.current < tableData.tbLeft + tableData.width + 6) {
                console.log("ok4");
                pX.current += 5;
            }
        }
        //우상 기준

        if (pY.current < 0)
            //외부 벽 충돌 감지
            pY.current = 0;
        if (pY.current >= mapSize - charSize) pY.current = mapSize - charSize;
        if (pX.current < 0) pX.current = 0;
        if (pX.current >= mapSize - charSize) pX.current = mapSize - charSize;

        //캐릭터 좌표 최신화
        myChar.style.left = pX.current + "px";
        myChar.style.top = pY.current + "px";
        // console.log("interval");
    }, 16);

    //첫 렌더링될 때만 입력받는 input태그로 focus
    useEffect(() => {
        console.log("useEffect focus once");
        mapFocus.current.focus();
    }, []);

    //맵 클릭 시 입력 포커싱 지정
    const changeFocus = () => {
        mapFocus.current.focus();
    };

    const handleKeyDown = (e) => {
        keyPress[e.keyCode.toString()] = true;
    };
    const handleKeyUp = (e) => {
        keyPress[e.keyCode.toString()] = false;
    };
    const [Value, setValue] = useState("");
    useBeforeunload((e) => {
        if (Value !== "") e.preventDefault();
    });

    return (
        <div>
            <div
                onClick={changeFocus}
                style={{
                    width: mapSize,
                    height: mapSize,
                    background: "orange",
                }}
            >
                <div
                    id="myCharacter"
                    style={{
                        margin: `0px`,
                        width: charSize,
                        height: charSize,
                        background: "red",
                        position: "relative",
                    }}
                ></div>
                <div
                    id="table"
                    style={{
                        width: tableData.width,
                        height: tableData.height,
                        left: tableData.tbLeft,
                        top: tableData.tbTop,
                        background: "green",
                        position: "relative",
                    }}
                ></div>
            </div>
            <input
                onChange={(e) => setValue(e.target.value)}
                value={"a"}
                // style={{ display: "none" }}
                type="text"
                ref={mapFocus}
                onKeyDown={handleKeyDown}
                onKeyUp={handleKeyUp}
                readOnly
            />
            playMap
        </div>
    );
}
