import { createSlice } from "@reduxjs/toolkit";

export const ConLimitSlice = createSlice({
    name: "ConLimitSlice",
    initialState: {
        value: 0,
    },
    reducers: {
        incrementConLimit: (state) => {
            state.value += 1;
        },

        decrementConLimit: (state) => {
            state.value -= 1;
        },
    },
});

export const { incrementMaxCon, decrementMaxCon } = ConLimitSlice.actions;

export const selectConLimit = (state) => state.ConLimit.value;

export default ConLimitSlice.reducer;
