import { createSlice } from "@reduxjs/toolkit";

export const GatherNumCivilSlice = createSlice({
    name: "CrntRoomConCnt",
    initialState: {
        value: 4,
    },
    reducers: {
        incrementCivil: (state) => {
            state.value += 1;
        },

        decrementCivil: (state) => {
            state.value -= 1;
        },
    },
});

export const {
    incrementCrntCon,
    decrementCrntCon,
} = GatherNumCivilSlice.actions;

export const selectConCnt = (state) => state.civil.value;

//equeal
// export const selectCount = function (state) {
//     return state.counter.value;
// };

export default GatherNumCivilSlice.reducer;
