import React, { useState, useEffect } from "react";
import {
  Paper,
  Stack,
  Button,
  Container,
  Modal,
  Box,
  Typography,
  InputLabel,
  MenuItem,
  FormControl,
  Select,
} from "@mui/material";

// 아이콘
import AddIcon from "@mui/icons-material/Add";
import ShuffleIcon from "@mui/icons-material/Shuffle";

import RefreshIcon from "@mui/icons-material/Refresh";
import LibraryBooksIcon from "@mui/icons-material/LibraryBooks";
import SettingsIcon from "@mui/icons-material/Settings";

import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import { roomNum } from "../../redux/slice/RoomNumSlice";
import { roomList } from "../../redux/slice/RoomListSlice";
import { setDoc } from "../../redux/slice/CntDocSlice";
import { setMaf } from "../../redux/slice/CntMafSlice";
import { setCop } from "../../redux/slice/CntCopSlice";
import { setTalkTime, setVoteTime, setNightTime } from "../../redux/slice/GameTimeSlice";
import { setCon } from "../../redux/slice/CntConSlice";
import { setLimit } from "../../redux/slice/CntLimitSlice";
import { SetRoomTitle } from "../../redux/slice/RoomTitleSlice";

import { Helper } from "../RoomNavCom/Helper";
import { Random } from "./Random";
import SockJS from "sockjs-client";
import { over } from "stompjs";

// 모달 창 style
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 500,
  bgcolor: "background.paper",
  border: "1px solid #000",
  boxShadow: 24,
};
const Logo = styled.a`
  display: flex;
  flex-direction: row;
  cursor: pointer;
  img {
    display: block;
    width: 30px;
  }
`;

var stompClient = null;
function Nav() {
  const me = useSelector(state => state.user);
  let headers = {
    token:`${me.accessToken}`
  };
  const dispatch = useDispatch();
  const [SwitchModalValue, setSwitchModalValue] = useState(0);
  const [open, setOpen] = useState(false);
  const handleOpen = value => {
    setSwitchModalValue(value);
    setOpen(true);
  };
  const handleClose = () => setOpen(false);
  // 매칭 취소
  const noMatch = () => {
    let isR = 0;
    if (me.redUser) {
      isR = 1;
    }
    const d = {
      header: {
        type: "disconnection",
      },
      data: {
        userSeq: me.userSeq,
        isRedUser: isR,
      },
    };
    stompClient.send("/pub/game-matching", headers, JSON.stringify(d));
    stompClient.disconnect();
    handleClose();
  };

  const SwitchModals = () => {
    if (SwitchModalValue === 0) {
      return <Helper />;
    } else if (SwitchModalValue > 0) {
      return <Random num={SwitchModalValue} noMatch={noMatch} />;
    }
  };

  // 방 생성 모달
  const [openRoom, setOpenRoom] = useState(false);
  const HandleOpenRoom = () => {
    setOpenRoom(true);
  };
  const HandleCloseRoom = () => {
    setOpenRoom(false);
  };
  const navigate = useNavigate();
  // 방 제목
  const [roomTitle, setRoomTitle] = useState("");
  // 방 비밀번호 유무
  const [ox, setOx] = useState("false");
  // 방 비밀번호
  const [passWord, setPassWord] = useState("");
  // 방 최대인원수
  const [num, setNum] = useState(0);
  const handleChange = event => {
    setNum(event.target.value);
  };
  const GoRoom = () => {
    const data = {
      roomInfo: {
        roomSeq: 0,
        capacity: num, // 방 최대 인원
        hostUser: me.userSeq,
        isLocked: ox, // 비밀번호 유무
        password: passWord, // 암호
        title: roomTitle,
      },
      gameInfo: {
        // 게임 설정이 담겨있는 DTO
        gameInfoSeq: 0, // 아무 값이나 입력해주세요, DB Primary Key 및 게임 정보 식별자로 방 생성시 자동할당 됩니다.
        day: 0, // 낮 시간
        night: 0, // 밤시간
        doctorNum: 0, // 의사 수
        mafiaNum: 0, // 마피아 수
        policeNum: 0, // 경찰 수
        talkTimeoutSec: 0, // 낮 토론 시간 (단위 sec)
        voteTimeoutSec: 0, // 밤 토론 시간 (단위 sec)
      },
    };
    if (roomTitle && num) {
      if (passWord) {
        setOx(true);
      }
      axios
        .post("/api/room", data, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${me.accessToken}`,
          },
        })
        .then(res => {
          alert(`${res.data.roomInfo.roomSeq}방 입장`);
          dispatch(roomNum(res.data.roomInfo.roomSeq)); // 방 번호
          dispatch(SetRoomTitle(res.data.roomInfo.title)); // 방 제목
          dispatch(setDoc(res.data.gameInfo.doctorNum)); // 의사 수
          dispatch(setMaf(res.data.gameInfo.mafiaNum)); // 마피아 수
          dispatch(setCop(res.data.gameInfo.policeNum)); // 경찰 수
          dispatch(setVoteTime(res.data.gameInfo.voteTimeoutSec)); // 투표시간
          dispatch(setTalkTime(res.data.gameInfo.talkTimeoutSec)); // 낮 시간
          dispatch(setNightTime(res.data.gameInfo.night)); // 밤 시간
          dispatch(setCon(1)); // 방 현재 인원
          dispatch(setLimit(res.data.roomInfo.capacity)); // 방 전체 인원
          HandleCloseRoom();
          setNum(0);
          setRoomTitle("");
          // navigate("/gatherroom");
        });
    }
  };

  // 룸리스트 새로고침
  const getNewList = () => {
    // 방 목록
    axios
      .get("/api/room/list", {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${me.accessToken}`,
        },
      })
      .then(response => {
        dispatch(roomList(response.data));
      });
  };

  // 랜덤 매칭

  const connect = () => {
    let Sock = new SockJS("https://i7d106.p.ssafy.io:8080/ws");
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };
  const onConnected = () => {
    let isRed = 0;
    if (me.redUser) {
      isRed = 1;
    }
    const d = {
      header: {
        type: "connection",
      },
      data: {
        userSeq: me.userSeq,
        isRedUser: isRed,
      },
    };
    stompClient.send("/pub/game-matching", headers, JSON.stringify(d));
    stompClient.subscribe("/sub/game-matching", Received);
    console.log("네브바에서 소켓 연결이 되었습니다.");
  };
  const onError = err => {
    console.log(err);
  };
  // 받는 소켓
  const Received = payload => {
    var payloadData = JSON.parse(payload.body);
    console.log(payloadData);
    switch (payloadData.header.type) {
      case "GeneralUserNotCompleted": // 일반 유저 매칭 실패
        handleOpen(payloadData.data.num);
        break;
      case "RedUserNotCompleted": // 레드 유저 매칭 실패
        handleOpen(payloadData.data.num);
        break;
      case "GeneralUserCompleted": // 일반 유저 매칭 성공
        alert("매칭이 성공했습니다.");
        break;
      case "RedUserCompleted": // 레드 유저 매칭 성공
        alert("매칭이 성공했습니다.");
        break;
    }
  };
  // 랜덤 버튼 누르면 소켓 연결
  const RandomMat = () => {
    connect();
  };

  return (
    <Paper sx={{ width: "100%", p: 1 }}>
      <Stack direction='row' justifyContent='space-between' alignItems='center' spacing={2}>
        <Stack direction='row' justifyContent='center' alignItems='center' spacing={2}>
          <Button
            sx={{ width: "50%" }}
            variant='outlined'
            onClick={() => {
              HandleOpenRoom();
            }}
          >
            <AddIcon />
          </Button>
          <Button
            sx={{ width: "50%" }}
            variant='outlined'
            onClick={() => {
              RandomMat();
            }}
          >
            <ShuffleIcon />
          </Button>
        </Stack>
        <Stack direction='row' justifyContent='center' alignItems='center' spacing={2}>
          <Button
            sx={{ width: "50%" }}
            variant='outlined'
            onClick={() => {
              getNewList();
            }}
          >
            <RefreshIcon />
          </Button>
          <Button
            sx={{ width: "50%" }}
            onClick={() => {
              handleOpen(0);
            }}
            variant='outlined'
          >
            <LibraryBooksIcon />
          </Button>
          <Button variant='outlined' sx={{ width: "50%" }}>
            <SettingsIcon />
          </Button>
        </Stack>
      </Stack>

      {/* 방 생성 모달 */}
      <Modal
        open={openRoom}
        onClose={HandleCloseRoom}
        aria-labelledby='modal-modal-title'
        aria-describedby='modal-modal-description'
      >
        <Box sx={style}>
          <Box
            sx={{
              width: 502,
              height: 31,
              backgroundColor: "var(--color-5);",
            }}
          >
            <Logo>
              <img src='logo.svg' alt='logo' />
            </Logo>
          </Box>
          <Container>
            <Typography
              id='modal-modal-title'
              variant='h6'
              component='h2'
              sx={{
                my: 2,
                display: "flex",
                justifyContent: "center",
              }}
            >
              방 만들기
            </Typography>
            <span>방제목</span>
            <input
              type='text'
              placeholder='방 제목을 입력하세요'
              onChange={e => {
                setRoomTitle(e.target.value);
              }}
            />
            <span>비밀번호</span>
            <input
              type='password'
              id='password'
              placeholder='비밀번호를 입력하세요'
              onChange={e => {
                setPassWord(e.target.value);
              }}
            />
            <Box sx={{ minWidth: 120 }}>
              <FormControl fullWidth>
                <InputLabel id='demo-simple-select-label'>게임 인원 수</InputLabel>
                <Select
                  labelId='demo-simple-select-label'
                  id='demo-simple-select'
                  value={num}
                  label='게임 인원 수'
                  onChange={handleChange}
                >
                  <MenuItem value={6}>6</MenuItem>
                  <MenuItem value={7}>7</MenuItem>
                  <MenuItem value={8}>8</MenuItem>
                </Select>
              </FormControl>
            </Box>
            <Box
              sx={{
                display: "flex",
                justifyContent: "space-around",
                p: 1,
                m: 1,
                bgcolor: "background.paper",
                borderRadius: 1,
              }}
            >
              <Button variant='outlined' onClick={GoRoom}>
                방 생성
              </Button>
              <Button
                variant='outlined'
                onClick={HandleCloseRoom}
                size='small'
                sx={{ width: "25%" }}
              >
                취소
              </Button>
            </Box>
          </Container>
        </Box>
      </Modal>

      <Modal open={open} onClose={handleClose}>
        <Box sx={style}>
          <SwitchModals />
        </Box>
      </Modal>
    </Paper>
  );
}
export default Nav;
